import { useEffect, useState } from "react"
import { Form ,Button} from "react-bootstrap"
import cookie from "react-cookies"
import { useSelector } from "react-redux"
import { useParams } from "react-router"
import API, { endpoints } from "../Configs/API"
import Modal from 'react-modal'
import '../CSS/Booking.css'
import $ from 'jquery'



export default function Booking (){

    const [detail , setDetail] = useState([])
    const [adult ,setAdult] = useState(1)
    const [child , setChild] = useState(0)
    const [room ,setRoom] = useState(0)
    const [room_price , setRoom_price] = useState(0)

    const [is_open , setIs_open] = useState(false)

    const [status,setStatus] = useState("p")

    const {tourdetailId} = useParams()
    const user = useSelector(state => state.user.user)


    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        try {
            let res =await API.get(endpoints['tour_detail_by_id'](tourdetailId))
            setDetail(res.data)
            console.log(res.data)
        }catch(err){
            console.error(err)
        }
    },[])

    let openMOdal = ()=>{
        setIs_open(true)
        
    }
    let closeMOdal = ()=>{
        setIs_open(false)
    }

    const booking_action = async(event) =>{
        event.preventDefault()
        try {
            let res = await API.post(endpoints['add_booking'](tourdetailId),{
                'adult' : adult,
                'children' : child,
                'room' : room,
            },{
                headers :{
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)
        }catch(err){
            console.error(err)
        }
        openMOdal()
    }
   

    let update_booking = (e)=>{
        e.preventDefault()
        let update = async()=>{
            try {
                let res = await API.get(endpoints['update_booking'](tourdetailId),{
                    headers: {
                        'Authorization'  : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }

    const enable_input =()=>{
        $('.address_').removeAttr('disabled');
    }


    return(
<>
<div className="src-title-layout bg-img ">
    <div className="overlay-bl">
    </div>
    <div className="container">
        <div className="row d-flex align-items-center justify-content-center">
            <div className="col-md-9  fadeInUp animated text-center">
                <div className="src-page">
                    <ul>
                        <li>
                            Home <i className="fa fa-angle-double-right" aria-hidden="true"></i>
                        </li>
                        <li>
                            Tours List <i className="fa fa-angle-double-right" aria-hidden="true"></i>
                        </li>
                        <li>
                            {detail.name}'s Tour
                        </li>
                    </ul>
                </div>
                <h1 className="h1">Tours List</h1>
            </div>
        </div>
    </div>
</div>


<Modal
isOpen={is_open}
onRequestClose={closeMOdal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeMOdal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>

<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_booking}>
            <button type="submit">
                Bạn có mún thanh toán hog
            </button>    
        </Form>
    </div>
</div>
</Modal>





<div className="container py-5">
    <div className="row py-5">
        <div className="col-md-6 col-sm-12">
            <div className="header_">
                <h3 className="header">User's Information</h3>
            </div>
            <div className="body_">
                <div className="form-group">
                    <span>Name:</span>
                    <input type="text" value={`${user.first_name} ${user.last_name}`} className="form-control" disabled/>
                </div>
                <div className="form-group">
                    <span>Phone:</span>
                    <input type="text" value={`${user.phone}`} className="form-control" disabled/>
                </div>
                <div className="form-group">
                    <span>Address:</span>
                    <input type="text" value={`${user.address}`} className="form-control address_" disabled/>
                </div>
            </div>
            <div className="footer_">
                <button>
                    Save
                </button>
            </div>
        </div>
        <div className="col-md-6 col-sm-12">


        </div>
    </div>
</div>

</>
    )
}
