import { useEffect, useState } from "react"
import { Badge, Col, Container, Form, Image, Row, Spinner,Button } from "react-bootstrap"
import { useParams } from "react-router"
import API, { endpoints } from "../Configs/API"
import 'bootstrap/dist/css/bootstrap.min.css';
import Moment from "react-moment";
import { useSelector } from "react-redux";
import cookie from 'react-cookies'
import { Link } from "react-router-dom";
import kkc from '../image/Floating.svg'
import ufo from '../image/ufo.svg'
import Modal from "react-modal";
import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';
import * as React from 'react';
import '../CSS/Tourdetail.css'


export default function  TourDetail(){

    const [tour , setTour] = useState([])
    const [cmtcontent , setCmtcontent] = useState([])

    const [cmt , setCmt] = useState([])

    const {tourdetailId} = useParams()
    const [change ,setChange] = useState(1)
    
    const [rating,setRating] = useState(0)
    const [rating_total,setRating_total] = useState(0)
    const [is_open_add , setIs_open_add] = useState(false)
    const [content , setContent] = useState([])
    const [value, setValue] = React.useState(2);
    const user = useSelector(state => state.user.user)

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{

        let res = await API.get(endpoints['tour_detail_by_id'](tourdetailId),{
            headers : {
                'Authorization' : `Bearer ${cookie.load('access_token')}`
            }
        })
        setTour(res.data)
        setRating_total(res.data.rate.rate__avg)
        let loadcmt = async ()=>{
            try{
                let res = await API.get(endpoints['cmt'](tourdetailId))
                setCmt(res.data)
                console.log(res.data)
            }catch(err){
                console.error(err)
            }
        } 
        loadcmt()
    },[change,tourdetailId])

    let addcmt = async(event)=>{
        event.preventDefault()
        try{
            let rescmt = await API.post(endpoints['add_cmt'](tourdetailId),{
                'content': cmtcontent
            },{
                headers:{
                    'Authorization': `Bearer ${cookie.load('access_token')}`
                }
            })
            cmt.push(rescmt.data)
            setCmt(cmt)
            setChange(cmt.length)
        }catch(er){
            console.error(er)
        }
      
    }

    const saveRating = async (event) =>{
        event.preventDefault()
        if (window.confirm("Bạn Muốn Đánh Giá Bài Học Nay")===true){
            try{
                let resrate = await API.post(endpoints['rating'](tourdetailId),{
                    "rating" : rating
                },{
                headers:{
                    'Authorization': `Bearer ${cookie.load('access_token')}`
                }
            })
            console.info(resrate.data)
            setChange(change+1)
            }catch(er)
            {
                console.error(er)
            }
        }
    }
    
    let img_stt =(ele,stt)=>{
        ele = `${tour.imagedetail[stt]}`
        return ele 
    }

    //khai báo biến

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
       
    }
    let closeModal_add  = ()=>{
        setIs_open_add(false)
    }
    const update_list_detail = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                // formdata.append("image_detail",image.current.files[0])


                let res = await API.patch(endpoints['tour_detail'](tourdetailId),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                closeModal_add()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    
   
    let ratingForm = <>
           <Form onSubmit={saveRating}>
                    <Rating
                    name="simple-controlled"
                    value={rating}
                    onChange={(event, newValue) => {
                    setRating(newValue);
                    }}
                />
                <Button type="submit">
                    Gửi
                </Button>
            </Form>
        </>

    
    
    return(
<>
<Modal
isOpen={is_open_add}
onRequestClose={closeModal_add}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal_add}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's Tour</h3>
        <Form onSubmit={update_list_detail} className="form_register">
            
            <div className="label_">
                <label>Image</label>
            </div>
            {/* <div className="input_">
                <Form.Control type="file" ref={image} />
            </div> */}
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>

<div className="">

</div>

<div className="body_tour_detail">
    <div className="container">
        <div className="row">
            <div className="col-md-9 col-sm-12">

            </div>
            <div className="col-md-3">

            </div>
        </div>
    </div>
</div>

{/* <div className="btn_booking">
    <a href={`/tour_detail/${tourdetailId}/booking/`}>Đặt vé</a>
</div>

<div className="rating_total">
    <Typography component="legend">Read only</Typography>
    <Rating name="half-rating" value={rating_total}  precision={0.5} readOnly />
</div>
{ratingForm} */}


</>
    )
}
