import { useEffect, useRef, useState } from "react"
import { Col, Image,Form, Button } from "react-bootstrap"
import API, { endpoints } from "../Configs/API"
import { useLocation } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"


export default function Blog (){
    
    const [blog,setBlog] = useState([])    
    const [content,setContent] = useState([])    
    const [active,setActive] = useState([])    
    const [decription,setdecription] = useState([])    
    const [name,setName] = useState([])    
    const [ID, setID] = useState(0)
    const image = useRef()

    const [type_like , setType_like] = useState(0)
    const [like_count,setLike_count] = useState()
    
    const [tour_detail,setTourt_detail] =useState([])

    const [change,setChange] = useState(0)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)

    const [tour_add , setTour_add] = useState([])
    const [tour_selected , setTour_selected] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(async() => {
        try{
            let res = await API.get(endpoints['get_blog'])
            setBlog(res.data.results)
           
            let restour_detail = await API.get(`${endpoints['tour_detail_all']}`,[])
            setTourt_detail(restour_detail.data.results)
        }
        catch(er){
            console.error(er)
        }
       

    },[change]);


    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('div').attr('id')
        get_blog_by_id(a)
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setdecription(null)
        setTour_selected(null)
        setID(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setContent([])
        setName([])
        setdecription([])
        setTour_add(null)
    }

    let info_btn = (a)=>{
        return(
            <>
            <span className="icon_infor" onClick={a}>
                <span class="material-icons-outlined">
                info
                </span>
            </span>
            </>
        )
    }   
    let del_btn = (a)=>{
        return(
            <>
            <span className="icon_del" onClick={a}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </span>
            </>
        )
    }

    let get_and_del =(e) =>{
        var a = $(e.target).closest('div').attr('id')
        console.log(a)
        del_blog(a)
    }

    let get_value = (event)=>{
        var a = (event.target.options[event.target.selectedIndex].getAttribute("value"))
        setTour_add(a)
        console.log(a)
    }


    let like =  (e)=>{
        var a = $(e.target).closest('.slick_img').attr('id')
        let l =  async()=>{
            let res = await API.post(endpoints['like'](a),{
                "type" : type_like
            },{
                headers: {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)
            setChange(change+1)
        }
        l()
        
    }
    console.log(change)

    let heart =  (e)=>{

        var a = $(e.target).closest('.slick_img').attr('id')
        let l =  async()=>{
            let res = await API.post(endpoints['like'](a),{
                "type" : 1
            },{
                headers: {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setChange(change+1)

        }
        l()

    }
    let haha =  (e)=>{
        setChange(change+1)

        var a = $(e.target).closest('.slick_img').attr('id')
        let l =  async()=>{
            let res = await API.post(endpoints['like'](a),{
                "type" : 2
            },{
                headers: {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
        }
        l()

    }
    let sad =  (e)=>{

        var a = $(e.target).closest('.slick_img').attr('id')
        let l =  async()=>{
            let res = await API.post(endpoints['like'](a),{
                "type" : 3
            },{
                headers: {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setChange(change+1)

        }
        l()

    }
    let angry =  (e)=>{

        var a = $(e.target).closest('.slick_img').attr('id')
        let l =  async()=>{
            let res = await API.post(endpoints['like'](a),{
                "type" : 4
            },{
                headers: {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setChange(change+1)

        }
        l()

    }
    if (tour_selected !== '' && tour_selected !== null){

        $("#tags_info option").each(function(){

            if ($(this).attr('value') == tour_selected){
                $(this).attr("selected","selected");
            }
        })
    }
    const get_blog_by_id = (blogID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['detail_blog'](blogID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setActive(res.data.active)
            // setContent(res.data.content)
            setdecription(res.data.decription)
            setTour_selected(res.data.tour_detail)
            setID(res.data.id)
        }
        get_tour()
    }

    let update_blog =(e) =>{
        e.preventDefault()

        let update = async(blogID)=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("id" , ID)

    
                let res = await API.patch(endpoints['detail_blog'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                },[])
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()

    }
    const del_blog = async(blogID)=>{
        try{
            let res =await API.delete(endpoints['detail_blog'](blogID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            blog.slice(res.data,1)
            setBlog(blog)
            setChange(change+1)  
            toast.success('Delete success!', {
                position: "top-bottom",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
            console.log(res.data)
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }

    const add_tour = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("decription",decription)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("tour_detail",tour_add)
                let res = await API.post(endpoints['get_blog'],formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                blog.push(res.data)
                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

    // var ele = document.querySelectorAll('.option_')
    // console.log(ele)
    // var a = true
    // var b = true
    // if(b){
    //     $('.like_option').mouseover(function(){
    //         setTimeout(() => {
    //             $('.all_option').show()
    //             $('.all_option').mouseover(function(){
    //                 if (a){
    //                     console.log('abc')
    //                     a = false
    //                 }
    //                 else{
    //                     setTimeout(() => {
    //                         $('.all_option').mouseleave(function(){
    //                             $('.all_option').hide()
            
    //                         })
    //                     }, 2000);
    //                 }
    //             })
    //         }, 2000);
    //     })
    //     b = false
    // }else{
    //     $('.like_option').mouseleave(function(){
    //         $('.all_option').hide()
    //     })
    // }

    if(blog !== '' && blog!== undefined && blog !== null && 
    tour_detail !== '' && tour_detail!== undefined && tour_detail !== null ){
        
    return(
<>
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's Tour</h3>
        <Form onSubmit={update_blog} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="content">Decription</label>
                <input type="text" value={decription} onChange={(event)=>setdecription(event.target.value)} />
            </div>
            <div className="form_group" id="tag_gr">
                <label for="username">Tour Detail</label>
                <select id="tags_info">
                    {/* {tour_detail.map((u,index) => <option value={u.id} key={index}>{u.name}</option> )} */}
                </select>
            </div>

            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>
<div className="">
    <div className="container">
        <div className="title_blog">
            <h2>Blog</h2>
        </div>
        {blog.map( (u,index)=> <Tour key={index} tours = {u} info_btn = {info_btn(openMOdal)}
         del_btn= {del_btn(get_and_del)} like = {like} heart={heart} haha= {haha} sad={sad} angry={angry} />)}
        <div className="add_btn" onClick={openMOdal_add}>
            <span class="material-icons-outlined">
            add_circle_outline
            </span>
        </div>
    </div>
</div>


{/* ----------------------------modal_add-------------------- */}

<Modal
isOpen={is_open_add}
onRequestClose={closeModal_add}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal_add}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's Tour</h3>
        <Form onSubmit={add_tour} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Decription</label>
                <input type="text" value={decription} onChange={(event)=>setdecription(event.target.value)} />
            </div>
            <div className="form_group" id="tag_gr">
                <label for="username">Tour Detail</label>
                <select  onChange={get_value} >
                    {/* {tour_detail.map((u,index) => <option defaultValue={u.id} value={u.id} key={index}>{u.name}</option> )} */}
                </select>
            </div>

            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>

</>
        )
    }else{
        return(
<>
            <h1>TUTU đang load mấy má</h1>
</>
        )
    }
}

function Tour(props){
    let path = `/blog/${props.tours.id}`

    return(
<>
<div className="py-5">
    <div className="slick_img" style={{'backgroundImage' : `url(${props.tours.image})`,'backgroundPosition':'center',
    'backgroundSize':'cover'}} id={props.tours.id}>
        {props.del_btn}

        <div className="br_name">
            <div className="name_tour_br">
                {props.tours.name}
            </div>
        </div>
        
        <a href={path} className="src_detail">
        </a>
        {props.info_btn}
        <div className="container_option">
            <div className="option_blog d_fl_center">
                <div className="like_option" onClick={props.like}>
                    <span class="material-icons-outlined">
                    thumb_up_off_alt
                    </span>
                    <span>{props.tours.like.type__count}</span>
                </div>
                    {/* <div className="option_wrapper">
                        
                       
                    </div> */}
                 <div className="âbc" onClick={props.like}>
                    <span class="material-icons-outlined option_" >
                    thumb_up_off_alt
                    </span>
                </div>
                <div className="" onClick={props.heart}>
                    <span class="material-icons-outlined option_" >
                    favorite
                    </span>
                </div>
                <div className="" onClick={props.haha}>
                    <span class="material-icons-outlined option_" >
                    sentiment_satisfied
                    </span>
                </div>
                <div className=""  onClick={props.sad}>
                    <span class="material-icons-outlined option_">
                    sentiment_dissatisfied
                    </span>
                </div>
                <div className="" onClick={props.angry}>
                    <span class="material-icons-outlined option_">
                    sentiment_very_dissatisfied
                    </span>
                </div>
                <div className="count_cmt" onClick={props.cmt}>
                    <span class="material-icons-outlined">
                    chat
                    </span>
                    <span>{props.tours.cmt_blog.content__count}</span>
                </div>
            </div>
        </div>
    </div>
</div>


</>
    )
}
