import React, { useEffect, useRef, useState } from "react";
import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import { useLocation, useParams } from "react-router";
import { Link } from "react-router-dom";
import API, { AuthAPI, endpoints } from "../Configs/API";
import MyCard from "../Layouts/Template";
import $ from 'jquery'
import Modal from "react-modal";
import { toast } from "react-toastify";
import cookie from 'react-cookies'
import { Redirect, useHistory } from "react-router";
import '../CSS/ListDetail.css'
import '@mui/material'



export default function ListDetailByidTour (){
    const [detail , setDetail] = useState([])
    const [name , setName] = useState(null)
   
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)
    const [price_room , setPrice_room] = useState([])
    const [price_tour , setPrice_tour] = useState([])
    const [discount , setDiscount] = useState([])
    const [time_start , setTime_start] = useState([])
    const [duration , setDuration] = useState([])
    const [tour , setTour] = useState([])
    const [transport,setTransport] = useState([])

    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)

    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)
    
    const {tourID} = useParams()
    const image = useRef()

    const location = useLocation()
    const history = useHistory()
    const [desti , setDesti] = useState(1)
    const [date , setDate]  = useState(null)
    const [price , setPrice] = useState(null)
    const [discount_search ,setDiscount_search]  = useState(1)

    
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let res = await API.get(`${endpoints['detail'](tourID)}${location.search}`)
        setDetail(res.data)
        console.log(res.data)
        get_transport()

        let tour_ = async()=>{
            let res = await API.get(endpoints['tour_total'](tourID))
            setTour(res.data)
        }
        tour_()

    },[change,location.search,tourID])

    let get_transport = async ()=>{
        try{
            let res = await API.get(endpoints['transport'])
            setTransport(res.data.results)
        }catch(er){
            console.error(er)
        }
    }

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('.slick_img').attr('id')
        get_tour_select(a)
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
        setTour(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
       
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
    }
    const get_tour_select = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_detail'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setDuration(res.data.duration)
            setPrice_room(res.data.price_room)
            setPrice_tour(res.data.price_tour)
            setDiscount(res.data.discount)
            var date = res.data.time_start
            date =  date.replace(/[Z]/g,'')
            var th = date.indexOf(':',(date.indexOf(':'))+1 )
            var a  = date.slice(th,30)
            date = date.replace(a,'')            
            setTime_start(date)
            setID(res.data.id)
            setTour(res.data.tour)
            console.log(res.data)
        }
        get_tour()
    }

    const update_list_detail = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("price_room",price_room)
                formdata.append("price_tour",price_tour)
                formdata.append("discount",discount)
                formdata.append("duration",duration)
                formdata.append("time_start",time_start)    
                formdata.append("tour" , tour)
                formdata.append("id" , ID)

                let res = await API.patch(endpoints['tour_detail'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <span className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </span>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <span className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </span>
            </>
        )
    }

 
    const add_tour_detail = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("price_room",price_room)
                formdata.append("price_tour",price_tour)
                formdata.append("discount",discount)
                formdata.append("duration",duration)
                formdata.append("time_start",time_start)    
            
                let res = await API.post(endpoints['add_tour_detail'](tourID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                detail.push(res.data)
                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

   
    let get_and_del =(e) =>{
        var a = $(e.target).closest('.slick_img').attr('id')
        console.log(a)
        del_tour(a)
    }
    
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_detail'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            detail.slice(res.data,1)
            setDetail(detail)
            setChange(change-1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }


    const search = (e)=>{
        e.preventDefault()
      
        history.push(`/listour/${desti}/list_detail/?time=${date}`)
    }

    const onchange_tour = (e)=>{
        var a = e.target.options[e.target.selectedIndex].getAttribute("value") 
        setDesti(a)      
    }

    const onchange_price = (e)=>{
        var a = e.target.options[e.target.selectedIndex].getAttribute("value") 
        setPrice(a)      
    }

    let get_cmt = async (tourID)=>{
        let res = await API.get(endpoints['cmt'](tourID))
    }
    let tag_name ='' 

    if (tour !== null && tour !== '' && tour !== undefined ){
        if (tour.tags !== null && tour.tags!== undefined && tour.tags !== ''){
            tag_name = tour.tags[0].name
        }else{
            console.log('abc')
        }

return(
<>


<div className="src-title-layout bg-img ">
    <div className="overlay-bl">
    </div>
    <div className="container">
        <div className="row d-flex align-items-center justify-content-center">
            <div className="col-md-9  fadeInUp animated text-center">
                <div className="src-page">
                    <ul>
                        <li>
                            Home <i className="fa fa-angle-double-right" aria-hidden="true"></i> 
                        </li>
                        <li>
                            {tour.name}
                        </li>
                    </ul>
                </div>
                <h1 className="h1">Tours List</h1>
            </div>
        </div>
    </div>
</div>
<div className="listour py-5">
    <div className="container py-5">
        <div className="row">
            <div className="search_field_tour col-md-3 col-sm-4">
                <div className="title_search">
                    <span>Filter</span>             
                </div>
                <div className="cate_tour">
                    {tour.name}
                </div>
                <form className="form_search_tour p-2">
                    <div className="group_search">
                        <div className="label_search">
                            point of departure
                        </div>
                        <div className="select_time">
                            <select onChange="" className="form-control">
                                <option value="" key="">--Point Of Departure--</option>
                            </select>
                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            destination
                        </div>
                        <div className="select_time">
                            <select onChange="" className="form-control">
                                <option value="" key="">--Destination--</option>
                            </select>
                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            Time
                        </div>
                        <div className="list_btn">
                            <button value="" type="button" className="form-control">1-3 days</button>
                            <button value="" type="button"  className="form-control">4-7 days</button>
                            <button value="" type="button"  className="form-control">8-14 đays</button>
                            <button value="" type="button"  className="form-control">over 14 days</button>

                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            time_start
                        </div>
                        <div className="">
                           <input type="date" id="" value="" onChange="" className="form-control" />
                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            Price
                        </div>
                        <div className="">
                            <select className="form-control"   onChange={onchange_price} >
                                <option value="">--Please Select Price--</option>
                                <option value="500000"> &lt;&#61; 500000</option>
                                <option value="1000000">&lt;&#61; 1000000</option>
                                <option value="2000000">&lt;&#61; 2000000</option>
                                <option value="3000000">&lt;&#61; 3000000</option>
                            </select>                          
                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            Transport
                        </div>
                        <div className="list_btn">
                            <label type="button"  className="form-control" >
                                <input type="checkbox" class="promotion" value="1"/>
                                <span>Plan</span>
                            </label>
                            <label  type="button"  className="form-control" >
                                <input type="checkbox" class="promotion" value="1"/>
                                <span class="">Coach</span>
                            </label>      
                        </div>
                    </div>
                    <div className="group_search">
                        <div className="label_search">
                            Filter
                        </div>
                        <div className="filter_discount">
                            <div class="filter_">
                                <label class="switch">
                                    <input type="checkbox" class="promotion" value="1"/>
                                    <span class="slider round"></span>
                                </label>
                                Discount
                            </div>
                            <div class="filter_">
                                <label class="switch">
                                    <input type="checkbox" class="promotion" value="1"/>
                                    <span class="slider round"></span>
                                </label>
                                Remaining
                            </div>     
                        </div>
                    </div>
                </form>
            </div>  
            <div className="col-md-9">
                <div className="heading_listdetail">
                    <div className="tour_tag">
                        <h3>Tour's {tour.name}</h3> 
                    </div>
                    <div className="content_bot">
                        <p>{tour.content}</p>
                    </div>
                </div>

                <div className="row">
                    {detail.map( (u,index)=> <Tour key={index} tour = {u} info= {info_btn(openMOdal)}
                    get_cmt = {get_cmt} del_btn ={del_btn(get_and_del)} />)}
                </div>

            </div>
        </div>
        <div className="add_icon"onClick={openMOdal_add} >
            <span class="material-icons-outlined" >
                add_circle_outline
            </span>
        </div>
    </div>
</div>

{/* ---------------------modal_adđ------------------ */}
<Modal
isOpen={is_open_add}
onRequestClose={closeModal_add}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal_add}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's Tour</h3>
        <Form onSubmit={add_tour_detail} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Time Start</label>
                <input type="datetime-local" min="2021-10-10T00:00" max="2222-10-10T00:00" value={time_start} onChange={(event)=>setTime_start(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Duration</label>
                <input type="number"min="1" max="5" value={duration} onChange={(event)=>setDuration(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Price room</label>
                <input type="number" onChange={(event)=>setPrice_room(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Price tour</label>
                <input type="number" onChange={(event)=>setPrice_tour(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Discount</label>
                <input type="number" onChange={(event)=>setDiscount(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>
{/* ------------------------modal_info----------------- */}
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_list_detail} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Time Start</label>
                <input type="datetime-local" min="2021-10-10T00:00" max="2222-10-10T00:00" value={time_start} onChange={(event)=>setTime_start(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Duration</label>
                <input type="number"min="1" max="5" value={duration} onChange={(event)=>setDuration(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Price room</label>
                <input type="number" value={price_room} onChange={(event)=>setPrice_room(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Price tour</label>
                <input type="number" value={price_tour} onChange={(event)=>setPrice_tour(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Discount</label>
                <input type="number" value={discount} onChange={(event)=>setDiscount(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>
</>
        )
    }
}

function Tour(props){
    let path = ""

    path = `/tour_detail/${props.tour.id}/`

    let remain = ''

    let a = props.get_cmt(props.tour.id)

    var b = props.tour.time_start
    b =  b.replace(/[ZT]/g,' ')
    
    var d  =  b.slice(0,10)
    var c  =  b.slice(10,b.length)

    if (props.tour.slot <= 0 ){
        remain = <>
        <span>Out of slot:</span><span className="slot">{props.tour.slot}</span>  
    </>
    }else{
        remain = <>
            <span>Remaining:</span><span className="slot">{props.tour.slot}</span>  
        </>
    }
   
    var price = ''
    if (props.tour.discount >=1 ){
        price = <>
            <div className="price_present">
                {props.tour.price_tour} VND
            </div>
            <div className="">
                <span class="material-icons-outlined">
                    trending_down
                </span>
            </div>
            <div className="price_discount">
                {props.tour.total} VND
            </div>
        </>
    }else{
        price = <>
            <div className="price_present">
            {props.tour.price_tour} VND
            </div>
        </>
        
    }

    return(
<>
<div className="col-md-4">
    <div className="card_wrapper">
        <div className="img_" style={{'backgroundImage' : `url(${props.tour.image})`}} >
            {/* <a href=></a> */}
            <div className="rate_">
                <span>{props.tour.rate.rate__avg}</span>
            </div>
            <div className="count_cmt">
                {a.length}
            </div>
            <div className="like_list">
                <span class="material-icons-outlined">
                    favorite_border
                </span>
            </div>
        </div>
        <div className="body_">
            <div className="time_start">
                <p style={{marginBottom : '0px' , fontSize : '13px'}}>{d} - {props.tour.duration}D{props.tour.duration-1}N</p>
                <span  style={{marginBottom : '0px' , fontSize : '13px'}}>Time Start: {c}</span>
                
            </div>
            <div className="path_detail">
                <a href={path}>{props.tour.name}</a>
                
            </div>
            <div className="departure"  style={{marginBottom : '0px' , fontSize : '13px'}}>
                Departure: 
            </div>
            <div className="price_">
                {price}
            </div>
        </div>  
        <div className="footer_">
            <div className="btn_">
                <div className="btn__">
                    <div className="btn_booking">
                        <a type="button" href={`/tour_detail/:tourdetailId/booking/`} className="form-control btn_booking">
                            <span class="material-icons-outlined">
                            shopping_cart
                            </span><span>Booking</span>  
                        </a>
                    </div>
                    <div className="btn_detail">
                        <button type="button" className="form-control btn_detail">
                            <span class="material-icons-outlined">
                            info
                            </span><span>Detail</span>
                        </button>
                    </div>
                </div>
            </div>
            <div className="remaining_">
                {remain}
            </div>
        </div>
    </div>
</div>

</>
    )
}
