import { useEffect, useState } from "react"
import { Badge, Col, Container, Image, Row, Spinner } from "react-bootstrap"
import { useParams } from "react-router"
import Apis, { endpoints } from "../Configs/Apis"



export default function  LessonDetail(){
    let [lesson , setLesson] = useState([])
    let {lessonId} = useParams()


    // eslint-disable-next-line react-hooks/exhaustive-deps
    // useEffect( async()=>{
    //    let res = await Apis.get(endpoints['lesson-detail'](lessonId))
    //     setLesson(res.data)
    // })
    console.log(lesson)
    if(lesson === [])
        // return <Spinner animation="border" variant="primary"/>
        return <h1>ABC</h1>
    else 
        return(
        <Container>
            <h1>Title</h1>
            <Row>
                <Col md={5} xs={12}>
                    <Image src={lesson.image} rounded fluid />
                </Col>
                <Col md={7} xs={12}>
                    <h2>{lesson.subject }</h2>
                    <p>Ngay Tao:{lesson.created_date}</p>
                    <p>Ngay Cập Nhật:{lesson.update_date}</p>
                </Col>
                <Col>
                    {/* {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)} */}
                </Col>
            
            </Row>
            <hr/>
            <div>
                {lesson.content}
            </div>
        </Container>
    )
}
