import { useEffect, useState } from "react"
import { Badge, Col, Container, Form, Image, Row, Spinner,Button } from "react-bootstrap"
import { useParams } from "react-router"
import Apis, { endpoints } from "../Configs/Apis"
import 'bootstrap/dist/css/bootstrap.min.css';
import Moment from "react-moment";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import cookie from "react-cookies";


export default function  LessonDetail(){
    const [lesson , setLesson] = useState(null)
    const [tour , setTour] = useState(null)

    const {lessonId} = useParams()
    const {tourdetailId} = useParams()
    const [cmt , setCMT] = useState([])
    const [cmtcontent , setCmtcontent ] = useState(null)
    const user = useSelector(state => state.user.user)
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
       let res = await Apis.get(endpoints['lesson-detail'](lessonId))
        setLesson(res.data)
        // let res = await Apis.get(endpoints['tour-detail'](tourdetailId))
        // setTour(res.data)

        let loadCMT = async ()=>{
            try{
                let reps = await Apis.get(endpoints['comment'](lessonId))
                setCMT(reps.data)
            }catch(er){
                console.error(er)
            }
           
        }
        loadCMT()

    },[lessonId])

    const addCMT = async (event) =>{
        event.preventDefault()
        try{
            let rescmt = await Apis.post(endpoints['add-comment'],(lessonId),{
                'content':cmtcontent
            },{
                headers:{
                    "Authorization" : `Bearer ${cookie.load('access_token')}`
                }
            })
        }catch(er){
            console.error(er)
        }
    } 

    


    let comment = <em><Link to="/login">Đăng Nhập Để Bình Luận</Link></em>
    if (user !== null && user!== undefined){
        comment = <>
            <Form onSubmit={addCMT}>
                <Form.Group controlId="commentContent" className="mb-3">
                    <Form.Control as="textarea" value={cmtcontent} onChange={(event) =>setCmtcontent(event.target.value)}
                     placeholder="Nhập Nội Dung Muốn Bình Luận" rows={3}/>
                </Form.Group>
                <Button variant="info">
                    Gửi
                </Button>
            </Form>
        </>
    }



    if(lesson === null)
        return <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    else 
        return(
        <Container>
            <h1>Title</h1>
            <Row>
                <Col md={5} xs={12}>
                    <Image src={lesson.image}  fluid />
                </Col>
                <Col md={7} xs={12}>
                    <h2>{lesson.subject }</h2>
                    <p>Ngay Tao:{lesson.created_date}</p>
                    <p>Ngay Cập Nhật:{lesson.update_date}</p>
                </Col>
                <Col>
                    {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}
                </Col>
            
            </Row>
            <hr/>
            <div>
                {lesson.content}
            </div>
            <hr/>
            {comment}
            <hr/>
            {cmt.map((c,index)=>
            <Row key={index}>
              <Col md={2} xs={5}>
                  <img src={c.creator.username} alt=""   />
                  <p>{c.creator.first_name}</p>
              </Col>
              <Col md={10} xs={7}>
                  <p>{c.content}</p>
                  <p>Da cmt luc: <Moment fromNow>{c.created_date}</Moment></p>
              </Col>
            </Row>)}
          
        </Container>
    // if(tour === null)
    //     return <div class="spinner-border text-primary" role="status">
    //     <span class="visually-hidden">Loading...</span>
    //   </div>
    // else 
    //     return(
    //     <Container>
    //         <h1>Title</h1>
    //         <Row>
    //             <Col md={5} xs={12}>
    //                 <Image src={tour.image} rounded fluid />
    //             </Col>
    //             <Col md={7} xs={12}>
    //                 <h2>{tour.subject }</h2>
    //                 <p>Ngay Tao:{tour.created_date}</p>
    //                 <p>Ngay Cập Nhật:{tour.update_date}</p>
    //             </Col>
    //             {/* <Col>
    //                 {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}
    //             </Col> */}
            
    //         </Row>
    //         <hr/>
    //         <div>
    //             {tour.content}
    //         </div>
    //     </Container>
    )
}
