import React, { useEffect, useRef, useState } from "react";
import { Button, Card, Col, Container, Form, Row } from "react-bootstrap";
import { useLocation, useParams } from "react-router";
import { Link } from "react-router-dom";
import API, { AuthAPI, endpoints } from "../Configs/API";
import MyCard from "../Layouts/Template";
import $ from 'jquery'
import Modal from "react-modal";
import { toast } from "react-toastify";
import cookie from 'react-cookies'
import { Redirect, useHistory } from "react-router";


export default function ListDetailByidTour (){
    const [detail , setDetail] = useState([])
    const [name , setName] = useState(null)
   
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)
    const [price_room , setPrice_room] = useState([])
    const [price_tour , setPrice_tour] = useState([])
    const [discount , setDiscount] = useState([])
    const [time_start , setTime_start] = useState([])
    const [duration , setDuration] = useState([])
    const [tour , setTour] = useState([])
    const [transport,setTransport] = useState([])

    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)

    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)
    
    const {tourID} = useParams()
    const image = useRef()

    const location = useLocation()
    const history = useHistory()
    const [desti , setDesti] = useState(1)
    const [date , setDate]  = useState(null)
    const [price , setPrice] = useState(null)
    const [discount_search ,setDiscount_search]  = useState(1)

    
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let res = await API.get(`${endpoints['detail'](tourID)}${location.search}`)
        setDetail(res.data)
        get_transport()
        let tour_ = async()=>{
            let res = await API.get(`${endpoints['tours']}`)
            setTour(res.data.results)
        }
        tour_()
    },[change,location.search,tourID])

    let get_transport = async ()=>{
        try{
            let res = await API.get(endpoints['transport'])
            setTransport(res.data.results)
        }catch(er){
            console.error(er)
        }
    }

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('.slick_img').attr('id')
        get_tour_select(a)
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
        setTour(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
       
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
    }
    const get_tour_select = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_detail'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setDuration(res.data.duration)
            setPrice_room(res.data.price_room)
            setPrice_tour(res.data.price_tour)
            setDiscount(res.data.discount)
            var date = res.data.time_start
            date =  date.replace(/[Z]/g,'')
            var th = date.indexOf(':',(date.indexOf(':'))+1 )
            var a  = date.slice(th,30)
            date = date.replace(a,'')            
            setTime_start(date)
            setID(res.data.id)
            setTour(res.data.tour)
            console.log(res.data)
        }
        get_tour()
    }

    const update_list_detail = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("price_room",price_room)
                formdata.append("price_tour",price_tour)
                formdata.append("discount",discount)
                formdata.append("duration",duration)
                formdata.append("time_start",time_start)    
                formdata.append("tour" , tour)
                formdata.append("id" , ID)

                let res = await API.patch(endpoints['tour_detail'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <span className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </span>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <span className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </span>
            </>
        )
    }

 
    const add_tour_detail = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("price_room",price_room)
                formdata.append("price_tour",price_tour)
                formdata.append("discount",discount)
                formdata.append("duration",duration)
                formdata.append("time_start",time_start)    
            
                let res = await API.post(endpoints['add_tour_detail'](tourID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                detail.push(res.data)
                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

   
    let get_and_del =(e) =>{
        var a = $(e.target).closest('.slick_img').attr('id')
        console.log(a)
        del_tour(a)
    }
    
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_detail'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            detail.slice(res.data,1)
            setDetail(detail)
            setChange(change-1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }


    const search = (e)=>{
        e.preventDefault()
      
        history.push(`/listour/${desti}/list_detail/?time=${date}`)
    }

    const onchange_tour = (e)=>{
        var a = e.target.options[e.target.selectedIndex].getAttribute("value") 
        setDesti(a)      
    }

    const onchange_price = (e)=>{
        var a = e.target.options[e.target.selectedIndex].getAttribute("value") 
        setPrice(a)      
    }

    console.log(price)

return(
<>
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_list_detail} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Time Start</label>
                <input type="datetime-local" min="2021-10-10T00:00" max="2222-10-10T00:00" value={time_start} onChange={(event)=>setTime_start(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Duration</label>
                <input type="number"min="1" max="5" value={duration} onChange={(event)=>setDuration(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Price room</label>
                <input type="number" value={price_room} onChange={(event)=>setPrice_room(event.target.value)} />
            </div>
            <div className="form_group">
