import { useEffect, useState } from "react"
import { Badge, Col, Container, Form, Image, Row, Spinner,Button } from "react-bootstrap"
import { useParams } from "react-router"
import Apis, { endpoints } from "../Configs/Apis"
import 'bootstrap/dist/css/bootstrap.min.css';
import Moment from "react-moment";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import Rating from "react-rating";


export default function  LessonDetail(){
    const [lesson , setLesson] = useState(null)
    const [tour , setTour] = useState(null)

    const {lessonId} = useParams()
    const {tourdetailId} = useParams()
    const [cmt , setCMT] = useState([])

    const [change ,setChange] =useState(1)
    const [cmtcontent , setCmtcontent ] = useState(null)
    const [rating ,setRating] = useState(0)
    
    const user = useSelector(state => state.user.user)
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
       let res = await Apis.get(endpoints['lesson-detail'](lessonId),{
            headers:{
                'Authorization': `Bearer ${cookie.load('access_token')}`
            }
        })
        setLesson(res.data)
        setRating(res.data.rate)
        // console.info(res.data.rate)
        let loadCMT = async ()=>{
            try{
                let reps = await Apis.get(endpoints['comment'](lessonId))
                setCMT(reps.data)

            }catch(er){
                console.error(er)
            }
        }
        loadCMT()
    },[change])

    const addCMT = async (event) =>{
        event.preventDefault()
        try{
            let rescmt = await Apis.post(endpoints['add-comment'](lessonId),{
                'content': cmtcontent
                
            },{
                headers:{
                    'Authorization': `Bearer ${cookie.load('access_token')}`
                }
            })
            cmt.push(rescmt.data)
            setCMT(cmt)
            setChange(cmt.length)
        }catch(er){
            console.error(er)
        }
    } 

    const saveRting = async (event) =>{
        event.preventDefault()
        if (window.confirm("Bạn Muốn Đánh Giá Bài Học Nay")===true){
            try{
                let resrate = await Apis.post(endpoints['rating'](lessonId),{
                    "rating" : rating
                },{
                headers:{
                    'Authorization': `Bearer ${cookie.load('access_token')}`
                    // 'Authorization': `Bearer hJ5jtoiPCWQxTApYDJ3fZ5cIYTUnet`
                }
            })
            // console.info(resrate.data)

            }catch(er)
            {
                console.error(er)
            }
        }
        // console.info(rating)
    }

    let comment = <em><Link to="/login">Đăng Nhập Để Bình Luận</Link></em>
    let ratingForm = ""
    let create_date= ""
    var index2 = 0
    var find_dot = 0 
    var dem = 0
    let chuoi = {'Z':' ','T':' '}
    if (user !== null && user!== undefined){
        comment = <>
            <Form onSubmit={addCMT}>
                <Form.Group controlId="commentContent" className="mb-3">
                    <Form.Control as="textarea" value={cmtcontent} onChange={(event) =>setCmtcontent(event.target.value)}
                     placeholder="Nhập Nội Dung Muốn Bình Luận" rows={3}/>
                </Form.Group>
                <Button type="submit" variant="info">
                    Gửi
                </Button>
            </Form>
        </>

        ratingForm = <>
            <Form onSubmit={saveRting}>
                <Rating initialRating={rating} onChange={(event)=>setRating(event)}/>
                <Button type="submit">
                    Gửi
                </Button>
            </Form>
        </>
        // create_date = `${lesson.created_date}`

        console.info(lesson)
        // if (create_date!==null){
            // create_date = create_date.replace(/[ZT]/g,' ')
            // index2 = create_date.indexOf(':',( (create_date.indexOf(':'))+1 ) )
            // console.log()
            // for(let i = index2; i <= create_date.length;i++){
                // if(create_date[i].indexOf('a')){
                    // dem ++
                // }
            // }
            // console.info(create_date)
        // }
    }



    if(lesson === null)
        return <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
        </div>
    else 
        return(
        <Container>
            <h1>Title</h1>
            <Row>
                <Col md={5} xs={12}>
                    <Image src={lesson.image} roundedCircle  fluid />
                </Col>
                <Col md={7} xs={12}>
                    <h2>{lesson.subject }</h2>
                    <p>Ngay Tao:{lesson.created_date}</p>
                    <p>Ngay Cập Nhật:{lesson.update_date}</p>
                    <p>
                        {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}

                    </p>
                    <p>
                        {ratingForm}
                    </p>
                </Col>
            </Row>
            <hr/>
            <div>
                {lesson.content}
            </div>
            <hr/>
            {comment}
            <hr/>
            {cmt.map((c,index)=>
            <Row key={index}>
              <Col md={2} xs={5}>
                  {/* <Image src={c.creator[2]} alt="" roundedCircle fluid /> */}
                  {/* <p className="text-center">{c.creator[1]}</p> */}
              </Col>
              <Col md={10} xs={7}>
                  <p>{c.content}</p>
                  <p>Da cmt luc: <Moment fromNow>{c.created_date}</Moment></p>
              </Col>
            </Row>)}
          
        </Container>
    // if(tour === null)
    //     return <div class="spinner-border text-primary" role="status">
    //     <span class="visually-hidden">Loading...</span>
    //   </div>
    // else 
    //     return(
    //     <Container>
    //         <h1>Title</h1>
    //         <Row>
    //             <Col md={5} xs={12}>
    //                 <Image src={tour.image} rounded fluid />
    //             </Col>
    //             <Col md={7} xs={12}>
    //                 <h2>{tour.subject }</h2>
    //                 <p>Ngay Tao:{tour.created_date}</p>
    //                 <p>Ngay Cập Nhật:{tour.update_date}</p>
    //             </Col>
    //             {/* <Col>
    //                 {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}
    //             </Col> */}
            
    //         </Row>
    //         <hr/>
    //         <div>
    //             {tour.content}
    //         </div>
    //     </Container>
    )
}
