import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container ,Button, Form} from "react-bootstrap";
import API, { AuthAPI, endpoints } from "../Configs/API";
import { useLocation,useHistory } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"
import { Pagination } from '@mui/material';
import '../CSS/Destination.css';

export default function Departure () {
    
    const [tour , setTour] = useState([])
    const [name , setName] = useState(null)
    const [tag , setTag] = useState([])

    const [tags_add , setTags_add] = useState([])
    const [tags_selected1 , setTags_selected1] = useState([])
    const [tags_selected2 , setTags_selected2] = useState([])

    const [content , setContent] = useState([])
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)


    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)
    const location = useLocation()
    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)


    const [count , setCount] = useState(0)
    const image = useRef()
    const history = useHistory()

    const [name_search,setName_search] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let query = location.search
        
            if(query ==='')
                query = `?page=${page}`
       

        let res = await API.get(`${endpoints['tour_total_all']}${query}`)

        let res_tag = await API.get(endpoints['get_tag_country'])
        setTag(res_tag.data.results)

        setTour(res.data.results)
        setNext(res.data.next)
        setPrev(res.data.previous)
        setCount(res.data.count)
        
    },[location.search,page,next,prev,change])

   
    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('.slick_img').attr('id')
        console.log(a);
        // get_tour_selected(a)
    }
    const get_tour_selected = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_detail_by_id'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })

            setName(res.data.name)
            setActive(res.data.active)
            setContent(res.data.content)
            setTags_selected1(res.data.tags[0])
            setID(res.data.id)
        }
        get_tour()
    }


    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected1(null)
        setTags_selected2(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected1(null)
        setTags_selected2(null)
        setTags_add([])  
    }


    

    const update_tour = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("id" , ID)
    
                let res = await API.patch(endpoints['tour_total'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                },[])
                console.log(res.data)
                closeModal()
                update_tag_tour(res.data.id)
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <div className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </div>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <div className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </div>
            </>
        )
    }

    
    const add_tag_tour = (tourID)=>{
        let add_tag = async () =>{
            let res = await API.post(endpoints['tour_total_add_tag'](tourID),{
                'tags' : tags_add
            },{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)
           
        }
        add_tag()
    }

    const update_tag_tour = (tourID)=>{
        let add_tag = async () =>{
            let res = await API.post(endpoints['tour_total_update_tag'](tourID),{
                'tag1' : tags_add[0], 
                'tag2' : tags_add[1] 
            },{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
           
        }
        add_tag()
    }

    const add_tour = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                let res = await API.post(endpoints['tours'],formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data.id)
                tour.push(res.data)
                add_tag_tour(res.data.id)

                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

    let get_value = (event)=>{
        if (tags_add.length >= 0 ){
            tags_add.splice(0)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        setChange(change+1)

    }

    let get_value2 = (event)=>{
        setChange(change+1)
        if (tags_add.length >= 1 ){
            tags_add.splice(1)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
    }


    if (tags_selected1 !== '' && tags_selected1 !== null){
        if (tags_selected1){  
            $("#tag_add option").each(function(){
                if ($(this).text() === tags_selected1.name){
                    $(this).attr("selected","selected");
                }
            })
        }
    }
    if (tags_selected2 !== '' && tags_selected2 !== null){
        if (tags_selected2){  
            $("#tag_add2 option").each(function(){
                if ($(this).text() === tags_selected2.name){
                    $(this).attr("selected","selected");
                }
            })
        }
    }
    let get_and_del =(e) =>{
        var a = $(e.target).closest('.slick_img').attr('id')

        del_tour(a)
    }
    // XOng
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_total'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            tour.slice(res.data,1)
            setTour(tour)
            setChange(change+1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }
   

    if (tour !== null && tour !== '' && tour !== undefined ){
        $('.option_wrapper').hide()
        var a = document.querySelectorAll('.slick_img')
        var b = document.querySelectorAll('.option_wrapper')
        for(let i = 0 ; i< a.length ; i ++){
            $(a[i]).mousemove(function(){
                $(b[i]).show()  
            })
            $(a[i]).mouseleave(function(){
                $(b[i]).hide()  
            })
        }
        

    return (
<>
<div className="src-title-layout bg-img ">
    <div className="overlay-bl">
    </div>
    <div className="container">
        <div className="row d-flex align-items-center justify-content-center">
            <div className="col-md-9  fadeInUp animated text-center">
                <div className="src-page">
                    <ul>
                        <li>
                            Home <i className="fa fa-angle-double-right" aria-hidden="true"></i>
                        </li>
                        <li>
                            Tours List 
                        </li>
                      
                    </ul>
                </div>
                <h1 className="h1">Tours List</h1>
            </div>
        </div>
    </div>
</div>
<Container className="py-5">

<div class="tour-packed py-5">
    <div class="container">
        <div class="row ">
            <div class="col-md-12 heading-section text-center">
                <span class="subheading">Destination</span>
                <h2 class="under-subheading mb-4">Tour Destination</h2>
            </div>
        </div>
    </div>
</div>
<div className="list_tour row">
    <div className="search_field_tour col-md-3 col-sm-4">
        <div className="title_search">
            <span>Filter</span>             
        </div>
        <div className="cate_tour">
        </div>
        <form className="form_search_tour p-2" onKeyDown={(e)=>{
                if (e.key === 'Enter') {
                    history.push(`/listour/?q=${name_search}`)
                }
            }} onSubmit={(e)=>e.preventDefault()}>
            <div className="group_search">
                <div className="label_search">
                    Destination
                </div>
                <div className="select_time">
                    <input type="text" value={name_search} className="form-control" 
                    onChange={(event)=>setName_search(event.target.value)}  />
                </div>
            </div>
            <div className="group_search">
                <div className="label_search">
                    Category
                </div>
                <div className="list_btn">
                    
                    {tag.map((u,index)=> <TagMap key={index} history={history} tag = {u}/>)}
                    
                </div>
            </div>
        </form>
    </div>  
    <div className="col-md-9 col-sm-8">
        <div className="list_tour_inner row" style={{margin : '0px'}}>
            {tour.map( (u,index)=> <Tour key={index} tours = {u} info= {info_btn(openMOdal)} del_btn ={del_btn(get_and_del)} />)}
            <Col md={3} xs={6} className="py-3">
               
            </Col>
        </div>
    </div>
    <Pagination count={Math.ceil(count/6)}   onChange={(event, value) => setPage(value)} page={page}
    style= {{outline : 'none',border:'none'}} color="secondary" />


</div>
   
</Container> 
<ToastContainer />
{/* -------------------------modal_info---------------------- */}
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_tour} className="form_register">
        <div className="form_group">
                <div className="label_wrapper">
                    <label for="name_add">Name</label>
                </div>
                <div className="input_wrapper">
                    <input id="name_add" type="text" value={name} onChange={(event)=>setName(event.target.value)} />
                </div>
            </div>
           
            <div className="form_group">
                <div className="label_wrapper">
                    <label for="username">Content</label>
                </div>
                <div className="input_wrapper">
                    <input type="text" id="content_add" value={content} onChange={(event)=>setContent(event.target.value)} />
                </div>
            </div>
            <div className="form_group" id="tag_gr">
                <div className="label_wrapper">
                    <label for="tag_add">Tags</label>
                </div>
                <div className="input_wrapper">
                    <select  onChange={get_value} id="tag_add" style={{marginRight : '15px'}}>
                        {tag.map((u,index) => <option value={u.name} key={index}>{u.name}</option> )}
                    </select>
                </div>
                
            </div>

            <div className="form_group">
                <div className="label_wrapper">
                    <label for="active_add">Active</label>
                </div>
                <div className="input_wrapper">
                    <input id="active_add" type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
                </div>
            </div>
            <div className="form_group">
                <div className="label_wrapper">
                    <label for="img_add">Image</label>

                </div>
                <div className="input_wrapper">
                    <Form.Control id="img_add" type="file" ref={image} />

                </div>
            </div>
            
            <Button variant="primary" type="submit" className="btn_add">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>

{/* ----------------------modal_add----------------------- */}

{/* <Modal
isOpen={is_open_add}
onRequestClose={closeModal_add}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal_add}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="" style={{fontWeight : '600'}}>Information's Tour</h3>
        <Form onSubmit={add_tour} className="form_register">
            <div className="form_group">
                <div className="label_wrapper">
                    <label for="name_add">Name</label>
                </div>
                <div className="input_wrapper">
                    <input id="name_add" type="text" value={name} onChange={(event)=>setName(event.target.value)} />
                </div>
            </div>
           
            <div className="form_group">
                <div className="label_wrapper">
                    <label for="username">Content</label>
                </div>
                <div className="input_wrapper">
                    <input type="text" id="content_add" value={content} onChange={(event)=>setContent(event.target.value)} />
                </div>
            </div>
            <div className="form_group" id="tag_gr">
                <div className="label_wrapper">
                    <label for="tag_add">Tags</label>
