import { Container, Row } from "react-bootstrap"
import { useParams } from "react-router"
import Apis, { endpoints } from "../Configs/Apis"
import MyCard from "../Layouts/Mycard"

export default function Lesson(){
    let [lessons , setLessons] = useState([])
    let [detail , setDetail] = useState([])

    let {courseId} = useParams()
    let {detailId} = useParams()


    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
       let res = await Apis.get(endpoints['lessons'](courseId))
        setLessons(res.data)
        // let res = await Apis.get(endpoints['detail'](detailId))
        // setDetail(res.data)
    
    })


    return(
<Container>
    <Row>
        {lessons.map(c => <MyCard obj = {c} type="lesson" />)}
        {/* {detail.map(c => <MyCard obj = {c} type="tour" />)} */}
        ABC
    </Row>
</Container>
    )
}
