import { useEffect, useState } from "react"
import { Badge, Col, Container, Image, Row, Spinner } from "react-bootstrap"
import { useParams } from "react-router"
import Apis, { endpoints } from "../Configs/Apis"
import 'bootstrap/dist/css/bootstrap.min.css';



export default function  LessonDetail(){
    const [lesson , setLesson] = useState(null)
    const [tour , setTour] = useState(null)

    const {lessonId} = useParams()
    const {tourdetailId} = useParams()
    const [cmt , setCMT] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
       let res = await Apis.get(endpoints['lesson-detail'](lessonId))
        setLesson(res.data)
        // let res = await Apis.get(endpoints['tour-detail'](tourdetailId))
        // setTour(res.data)

        let loadCMT =async ()=>{
            try{
                let reps = await Apis.get(endpoints['comment'](lessonId))
                setCMT(reps.data)
            }catch(er){
                console.error(er)
            }
           
        }
        loadCMT()

    },[lessonId])
    console.log(cmt[0].creator.id)

    if(lesson === null)
        return <div class="spinner-border text-primary" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    else 
        return(
        <Container>
            <h1>Title</h1>
            <Row>
                <Col md={5} xs={12}>
                    <Image src={lesson.image} roundedCircle fluid />
                </Col>
                <Col md={7} xs={12}>
                    <h2>{lesson.subject }</h2>
                    <p>Ngay Tao:{lesson.created_date}</p>
                    <p>Ngay Cập Nhật:{lesson.update_date}</p>
                </Col>
                <Col>
                    {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}
                </Col>
            
            </Row>
            <hr/>
            <div>
                {lesson.content}
            </div>
            <hr/>
            {cmt.map(c=>
            <Row>
              <Col md={2} xs={5}>
                  <img src={c.creator.avatar} alt="" circle fluid />
                  <p>{c.creator}</p>
              </Col>
              <Col md={10} xs={7}>
                  <p>{c.content}</p>
                  <p>Da cmt luc:{c.created_date}</p>
              </Col>
            </Row>)}
          
        </Container>
    // if(tour === null)
    //     return <div class="spinner-border text-primary" role="status">
    //     <span class="visually-hidden">Loading...</span>
    //   </div>
    // else 
    //     return(
    //     <Container>
    //         <h1>Title</h1>
    //         <Row>
    //             <Col md={5} xs={12}>
    //                 <Image src={tour.image} rounded fluid />
    //             </Col>
    //             <Col md={7} xs={12}>
    //                 <h2>{tour.subject }</h2>
    //                 <p>Ngay Tao:{tour.created_date}</p>
    //                 <p>Ngay Cập Nhật:{tour.update_date}</p>
    //             </Col>
    //             {/* <Col>
    //                 {lesson.tags.map( (u,index)=> <h4><Badge bg="secondary" key={index}>{u.name}</Badge></h4>)}
    //             </Col> */}
            
    //         </Row>
    //         <hr/>
    //         <div>
    //             {tour.content}
    //         </div>
    //     </Container>
    )
}
