import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container ,Button, Form} from "react-bootstrap";
import API, { AuthAPI, endpoints } from "../Configs/API";
import { useLocation } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"
import { Pagination } from '@mui/material';


export default function Destination () {
    
    const [tour , setTour] = useState([])
    const [name , setName] = useState(null)
    const [tags , setTags] = useState([])
    const [tags_add , setTags_add] = useState([])
    const [tags_selected , setTags_selected] = useState([])
    const [content , setContent] = useState([])
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)

    const [name_search ,setName_search] = useState(null)
    const [tags_search , setTags_search] = useState([])

    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)
    const location = useLocation()
    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)


    const [count , setCount] = useState(0)
    const user = useSelector(state => state.user.user)
    const image = useRef()



    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let query = location.search
        
            if(query ==='')
                query = `?page=${page}`
            else
                query = `&page=${page}`

        let res = await API.get(`${endpoints['tours']}${query}`)
        setTour(res.data.results)
        setNext(res.data.next)
        setPrev(res.data.previous)
        get_tags()
        setCount(res.data.count)
        console.log(res.data.count)

        // if (page >=2){
        //     setPrev(res.data.prev!==null)
        // }
        // else{
        //     setPrev(null)
        // }

    },[location.search,page,next,prev,change])

    let paging=(inc)=>{
        setPage(page+inc)
    }

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('div').attr('id')
        get_tour_select(a)
       
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
        setTags_add([])  
    }


    const get_tour_select = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_total'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setActive(res.data.active)
            setContent(res.data.content)
            setTags_selected(res.data.tags)
            setID(res.data.id)

        }
        get_tour()
    }


    const update_tour = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("id" , ID)
    
                let res = await API.patch(endpoints['tour_total'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                },[])
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <span className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </span>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <span className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </span>
            </>
        )
    }

    const get_tags =()=>{
        let tags = async()=>{
            try{
                let res = await API.get(endpoints['tag'])
                setTags(res.data.results)
            }catch(err){
                console.log(err)
            }
            
        }
        tags()
    }
    const add_tag_tour = (tourID)=>{
        let add_tag = async () =>{
            let res = await API.post(endpoints['tour_total_add_tag'](tourID),{
                'tags' : tags_add
            },{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)
           
        }
        add_tag()
    }
    const add_tour = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                // formdata.append("tags",tags_add)
                let res = await API.post(endpoints['tours'],formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data.id)
                tour.push(res.data)
                add_tag_tour(res.data.id)

                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

    let get_value = (event)=>{
        if (tags_add.length >= 1 ){
            tags_add.splice(1)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))

    }

    let get_value2 = (event)=>{
        if (tags_add.length >= 2 ){
            tags_add.splice(2)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
    }



    if (tags_selected !== '' && tags_selected !== null){
        if (tags_selected[0]){  
            $("#tags_info option").each(function(){
                if ($(this).text() === tags_selected[0].name){
                    $(this).attr("selected","selected");
                }
            })
        }
    }

    let get_and_del =(e) =>{
        var a = $(e.target).closest('div').attr('id')

        del_tour(a)
    }
    // XOng
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_total'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            tour.slice(res.data,1)
            setTour(tour)
            setChange(change+1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }
   

    const search_form = (e)=>{
        e.preventDefault()
        let search = async()=>{
            let res = await API.get(endpoints[''],{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)

        }
    }

    const change_tag_search =(event)=>{
        setTags_search(event.target.options[event.target.selectedIndex].getAttribute("value"))
    
    }


    console.log(tags_search)
    return (
<>
<div className="src-title-layout bg-img ">
    <div className="overlay-bl">
    </div>
    <div className="container">
        <div className="row d-flex align-items-center justify-content-center">
            <div className="col-md-9  fadeInUp animated text-center">
                <div className="src-page">
                    <ul>
                        <li>
                            Home <i className="fa fa-angle-double-right" aria-hidden="true"></i>
                        </li>
                        <li>
                            Tours List 
                        </li>
                      
                    </ul>
                </div>
                <h1 className="h1">Tours List</h1>
            </div>
        </div>
    </div>
</div>
<Container className="py-5">

<div class="tour-packed py-5">
    <div class="container">
        <div class="row ">
            <div class="col-md-12 heading-section text-center">
                <span class="subheading">Destination</span>
                <h2 class="under-subheading mb-4">Tour Destination</h2>
            </div>
        </div>
    </div>
</div>
<div className="list_tour row">
    {/* <Button className="btn_prev btn_tour" varient = "info" onClick={()=>paging(-1)} disabled={!prev}>&lt;&lt;</Button> */}
    <div className="search_field col-md-3"  style={{backgroundColor : '#ffb04f',borderRadius : '20px'}}>
        <form className="form_search" onSubmit={search_form} style={{display : 'flex',flexDirection : 'column',alignItems : 'center',justifyContent : 'center'}}>
            <div className="title_search" style={{display : 'flex' , flexDirection : 'column' , alignItems : 'center'}}>
                <h3 style={{'fontWeight': '700',color : 'white'}}>Search Tour</h3>
                <p style={{color : 'white'}}>Find your dream tour today!</p>
            </div>
            <div className="search_body" >
                <div className="search_name" style={{marginBottom : '15px'}}>
                    <input type="text" placeholder="Search Tour"
                     value={name_search}  onChange={(e)=> setName_search(e.target.value)}
                     style={{border : 'none', outline : 'none',padding : '5px 10px'}}/>
                </div>
                <div className="search_cat" style={{marginBottom : '15px' }}>
                    <select className="cate_select" onChange={change_tag_search}
                     style={{width : '100%',padding  :' 5px',border : 'none' , outline:'none'}} >
                        <option value="" key="">Tour Cateogry</option>
                        {tags.map((u,index) => <option value={u.id} key={index}>{u.name}</option>)}
                    </select>
                </div>
            </div>
        </form>
    </div>
    <div className="col-md-9">
        <div className="list_tour_inner row">
            {tour.map( (u,index)=> <Tour key={index} tours = {u} info= {info_btn(openMOdal)} del_btn ={del_btn(get_and_del)} />)}
            <Col md={3} className="py-3">
                <div className="add_wrapper"  onClick={openMOdal_add}>
                    <div className="add_center ">
                        <div className="add_icon" >
                            <span class="material-icons-outlined" >
                                add_circle_outline
                            </span>
                        </div>
                    </div>
                    <div className="footer_add">
                    </div>
                </div>
            </Col>
        </div>
    </div>
    <Pagination count={Math.ceil(count/6)} variant="outlined"  onChange={()=>console.log('abc')}
    style= {{outline : 'none'}} color="secondary" />


    {/* <Button className="btn_next btn_tour" varient = "info" onClick={()=>paging(+1)} disabled={!next}>&gt;&gt;</Button> */}
</div>
</Container> 
<ToastContainer />
{/* -------------------------modal_info---------------------- */}
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_tour} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
           
            <div className="form_group">
