import 'bootstrap/dist/css/bootstrap.min.css';
import { useEffect, useState } from 'react';
import { Button, Container, Form, FormControl, Nav, Navbar, NavDropdown } from "react-bootstrap";
import cookie from 'react-cookies';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router';
import { LogoutUser } from '../ActionCreators/UserCreator';
import Apis, { endpoints } from '../Configs/Apis';


export default function Header(){
  const [categories, setCategories] = useState([])
  const [q,setq] = useState("")
  const history = useHistory()
  const user =  useSelector(state => state.user.user)
  const dispatch = useDispatch()

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(async ()=>{
      try{
        let res = await Apis.get(endpoints['categories']).then(resp =>{
          setCategories(resp.data)
        })
      }catch(ex){
        console.error(ex)
      }
  },[])

  const search = (event) =>{
    event.preventDefault()
    history.push(`/?q=${q}`)
  }

  const logout = (event) =>{
    event.preventDefault()
    cookie.remove('access_token')
    cookie.remove('user')
    // dispatch(LogoutUser())
  }

  let path =   <Nav.Link href="/login">Đăng nhập</Nav.Link>
  if (user !== null && user !== undefined){
    path =  <><Nav.Link href="/">{user.username}</Nav.Link>
    <Nav.Link  onClick={logout}>Đăng Xuất</Nav.Link>
    </> 

  }
  
  return(
<Container>
    <Navbar bg="light" expand="lg">
      <Navbar.Brand href="#">Navbar scroll</Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" />
      <Navbar.Collapse id="navbarScroll">
      <Nav
      className="mr-auto my-2 my-lg-0"
      style={{ maxHeight: '100px' }}
      navbarScroll
      >
      <Nav.Link href="/">Home</Nav.Link>
      {categories.map((u,index)=> {
        let path = `?category_id=${u.id}`
        return(
          <Nav.Link key={index} href={path}>{u.name}</Nav.Link>
      )})}
      {path}
    </Nav>
    <Form onSubmit = {search} className="d-flex">
        <FormControl type="text" placeholder="Search" className="mr-sm-2" value={q} 
        onChange= {(event) =>{
          setq(event.target.value)
        }}/>
        <Button className="" type="submit">Search</Button>          
    </Form>
    </Navbar.Collapse>
  </Navbar>
</Container>
    )
}
