import { useEffect, useRef, useState } from "react"
import { Col, Image,Form, Button } from "react-bootstrap"
import API, { endpoints } from "../Configs/API"
import { useLocation } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"
import '../CSS/Blog.css'
import '@mui/material'
export default function Blog (){
    
    const [blog_main,setBlog_main] = useState([])    
    const [blog_first,setBlog_first] = useState([])    
    
    const [list_blog,setList_blog] = useState([])    

    const [blog_second2,setBlog_second2] = useState([])    
    const [blog_second3,setBlog_second3] = useState([])    


    const [content,setContent] = useState([])    
    const [active,setActive] = useState([])    
    const [decription,setdecription] = useState([])    
    const [name,setName] = useState([])    
    const [ID, setID] = useState(0)
    const [tag,setTag] = useState([])
    
    const location =  useLocation()
    const image = useRef()

    const [type_like , setType_like] = useState(0)
    const [like_count,setLike_count] = useState()
    
    const [tour_detail,setTourt_detail] =useState([])

    const [change,setChange] = useState(0)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)

    const [tour_add , setTour_add] = useState([])
    const [tour_selected , setTour_selected] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(async() => {
        try{
            let res = await API.get(`${endpoints['get_blog']}${location.search}`)
            setBlog_main(res.data.results.slice(0,1))
            setBlog_first(res.data.results.slice(1,4))
            console.log(res.data.results)
            // setBlog_second(res.data.results.slice(5,res.data.length))

            let restour_detail = await API.get(`${endpoints['tour_detail_all']}`,[])
            setTourt_detail(restour_detail.data.results)

            let res_tag = await API.get(endpoints['get_tag_blog'])
            setTag(res_tag.data.results)

         
            let get_blog_by_tag2 = async (name)=>{
                let res = await API.get(`${endpoints['get_blog']}?tag=${name}`,[])
                setBlog_second2(res.data.results)
            }
            let get_blog_by_tag3 = async (name)=>{
                let res = await API.get(`${endpoints['get_blog']}?tag=${name}`,[])
                setBlog_second3(res.data.results)
            }
            get_blog_by_tag2(res_tag.data.results[1].name)
            get_blog_by_tag3(res_tag.data.results[2].name)
        }
        catch(er){
            console.error(er)
        }
       
    },[change]);


    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('div').attr('id')
        // get_blog_by_id(a)
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setdecription(null)
        setTour_selected(null)
        setID(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setContent([])
        setName([])
        setdecription([])
        setTour_add(null)
    }

    

    // let get_and_del =(e) =>{
    //     var a = $(e.target).closest('div').attr('id')
    //     console.log(a)
    //     del_blog(a)
    // }

    // let get_value = (event)=>{
    //     var a = (event.target.options[event.target.selectedIndex].getAttribute("value"))
    //     setTour_add(a)
    //     console.log(a)
    // }


    // let like =  (e)=>{
    //     var a = $(e.target).closest('.slick_img').attr('id')
    //     let l =  async()=>{
    //         let res = await API.post(endpoints['like'](a),{
    //             "type" : type_like
    //         },{
    //             headers: {
    //                 'Authorization' : `Bearer ${cookie.load('access_token')}`
    //             }
    //         })
    //         console.log(res.data)
    //         setChange(change+1)
    //     }
    //     l()
        
    // }

    // let heart =  (e)=>{

    //     var a = $(e.target).closest('.slick_img').attr('id')
    //     let l =  async()=>{
    //         let res = await API.post(endpoints['like'](a),{
    //             "type" : 1
    //         },{
    //             headers: {
    //                 'Authorization' : `Bearer ${cookie.load('access_token')}`
    //             }
    //         })
    //         setChange(change+1)

    //     }
    //     l()

    // }
    // let haha =  (e)=>{
    //     setChange(change+1)

    //     var a = $(e.target).closest('.slick_img').attr('id')
    //     let l =  async()=>{
    //         let res = await API.post(endpoints['like'](a),{
    //             "type" : 2
    //         },{
    //             headers: {
    //                 'Authorization' : `Bearer ${cookie.load('access_token')}`
    //             }
    //         })
    //     }
    //     l()

    // }
    // let sad =  (e)=>{

    //     var a = $(e.target).closest('.slick_img').attr('id')
    //     let l =  async()=>{
    //         let res = await API.post(endpoints['like'](a),{
    //             "type" : 3
    //         },{
    //             headers: {
    //                 'Authorization' : `Bearer ${cookie.load('access_token')}`
    //             }
    //         })
    //         setChange(change+1)

    //     }
    //     l()

    // }
    // let angry =  (e)=>{

