import React, { useEffect, useState } from "react";
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useDispatch, useSelector, useStore } from "react-redux";
import cookies from "react-cookies";
import LogoutUser from "../Components/ActionCreator/Logout";
import { useHistory } from "react-router";
import API, { endpoints } from "../Configs/API";
import '../CSS/Header.css'
import { GoogleLogout } from "react-google-login";
import cookie from "react-cookies";



export default function Header(){

    // const store = useStore();
    // const auth = store.getState();
    const [q, setQ] = useState('')
    const user = useSelector(state => state.user.user)

    const dispatch = useDispatch()
    const history = useHistory()
    const [bac , setbac] = useState([])
    const [trung , settrung] = useState([])
    const [nam , setnam] = useState([])
    const [tay , settay] = useState([])

    const [blog_tag,setBlog_tag] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
    
        let res = await API.get(`${endpoints['tour_total_all']}?tag=north`)
        setbac(res.data.results)
        //check length giới hạn 5 des trong header

        // if(res.data.results.length >= 6){

        // }
        let res1 = await API.get(`${endpoints['tour_total_all']}?tag=mid`)
        settrung(res1.data.results)
        let res2 = await API.get(`${endpoints['tour_total_all']}?tag=south`)
        setnam(res2.data.results)
        let res3 = await API.get(`${endpoints['tour_total_all']}?tag=west`)
        settay(res3.data.results)

        let res_tag_blog = await API.get(endpoints['get_tag_blog'])
        setBlog_tag(res_tag_blog.data.results)
   
    },[])

    const logout =(event)=>{
        event.preventDefault()
        cookies.remove('access_token')
        cookies.remove('user')
        dispatch(LogoutUser())
        history.push("/")
        window.location.reload()
        
    }
    const onLogoutSuccess =()=>{
        console.log('thành coong')
        cookie.remove('email')
        cookie.remove('googleId')
        logout()
    }

    const onFailure = ()=>{
        console.log('fail')
    }

    let path = <>
    <Nav.Link href="/login">Login</Nav.Link>
    </>
              
    let logout_path = <NavDropdown.Item onClick={logout}>Đăng Xuất</NavDropdown.Item>

    
    if (user.username.indexOf('@') !== -1 && user.username !== null ){
        logout_path =  <GoogleLogout 
        clientId="926735501662-dg63o8sqtp0ultpraica9uk1gt791o93.apps.googleusercontent.com"
        buttonText="Logout  Google"
        onLogoutSuccess={onLogoutSuccess}
        onFailure={onFailure}
        render={renderProps => (
            <a type="button" onClick={renderProps.onClick}  disabled={renderProps.disabled}>
                This is my custom Google buttons
            </a>
            )}
        />
    }
       

    if (user !== null && user !== undefined){
        if(user.is_superuser){
            path =  <>
            <NavDropdown title={`welcome ${user.username}`} id="basic-nav-dropdown">
                <NavDropdown.Item href="/list_user">List User</NavDropdown.Item>
                <NavDropdown.Item href="/static">Thống kê</NavDropdown.Item>
                <NavDropdown.Divider className="divide" />
                {logout_path}
            </NavDropdown>
            </>
        }
        else{
            path =  <>
            <NavDropdown title={`welcome ${user.username}`} id="basic-nav-dropdown">
                <NavDropdown.Item href="/account_detail">Account</NavDropdown.Item>
                <NavDropdown.Divider className="divide" />
                {logout_path}
            </NavDropdown>
            </> 
        }
    }
    
    
    return(
<>
<Navbar expand="lg">
    <Navbar.Brand href="/">Stormy <span className="text-logo">Travel</span> </Navbar.Brand>
    <Navbar.Toggle/>
    <Navbar.Collapse>
        <Nav className="ml-auto">
            <Nav.Link href="/">Home</Nav.Link>
            <NavDropdown title="Tour" id="tour_dropdown" className="tour_dropdown">
                <div className="row_wrapper">
                    <div className="row">
                        <div className="col-md-3">
                            <div className="title_domain">
                                <a href="/listour/?tag=north">North Side</a>
                            </div>
                            <div className="list_wrapper">
                                <ul className="list_conscious"> 
                                    {bac.map((u,key) => getTour(u))}
                                </ul>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <div className="title_domain">
                                <a href="/listour/?tag=mid">Mid Side</a>
                            </div>
                            <div className="list_wrapper">
                                <ul className="list_conscious"> 
                                    {trung.map((u,key) => getTour(u))}
                                </ul>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <div className="title_domain">
                                <a href="/listour/?tag=south">South Side</a>
                            </div>
                            <div className="list_wrapper">
                                <ul className="list_conscious"> 
                                    {nam.map((u,key) => getTour(u))}
                                </ul>
                            </div>
                        </div>
                        <div className="col-md-3">
                            <div className="title_domain">
                                <a href="/listour/?tag=west">West Side</a>
                            </div>
                            <div className="list_wrapper">
                                <ul className="list_conscious"> 
                                    {tay.map((u,key) => getTour(u))}
                                </ul>
                            </div>
                        </div>  
                    </div>
                </div>
            </NavDropdown>            
                
            <Nav.Link href="/blog">Blog</Nav.Link>
            <Nav.Link href="/contact">Contact</Nav.Link>
            {path}
        </Nav>
    </Navbar.Collapse>
</Navbar>
</>
    )
}

function getTour(props){
    let path = ''
    path = `/listour/${props.id}/list_detail/`

    return(
<>
    <li><a href={path}>{props.name}</a></li>
</>
    )
}
