import { useState,useEffect } from "react"
import { Button,ButtonGroup, Container, Row } from "react-bootstrap";
import { useLocation } from "react-router";
import Apis, { endpoints } from '../Configs/Apis';
import MyCard from "../Layouts/Mycard";




export default function Home(){
    let [courses , setCourses] = useState([])
    let [tour , setTour] = useState([])
    let [prev , setPrev] = useState(false)
    let [next , setNext] = useState(false)
    let location = useLocation()
    
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async() => {
        let query = location.search
        let res = await Apis.get(`${endpoints['courses']}${query}`).then(resp=>{
            setCourses(resp.data.results)
        })
        // let res = await Apis.get(`${endpoints['tours']}${query}`).then(resp=>{
        //     setTour(resp.data.results)
        // })
    },[location.search])

    return(
<>
<ButtonGroup>
    <Button varient = "info" disabled={!prev}>&lt;&lt;</Button>
    <Button varient = "info" disabled={!next}>&gt;&gt;</Button>
</ButtonGroup>
<Container>
    <Row>
        {courses.map( (u,index)=> <MyCard key={index} obj = {u}/>)}
    </Row>
</Container>
</>


    )
}
