import { useEffect, useState } from 'react';
import { Doughnut } from 'react-chartjs-2';
import cookie  from 'react-cookies'
import API, { endpoints } from '../Configs/API';
import $ from 'jquery'
import '../CSS/Static.css'


export default function ChartJS (){

    const [list_booking , setList_booking] = useState([])

    const [chartData_year , setCharData_year] = useState([])   
    const [count_ticket , setCount_ticket] = useState(0)
    const [change_nor,setChange_nor] = useState(1)
    const [change_precious,setChange_precious] = useState(1)

    const [count_ticket_precious , setCount_ticket_precious] = useState(0)

    const [defaults , setDefaul ] = useState(0)

    const [chartData_precious , setCharData_precious] = useState([])   
    const [year , setYear ] = useState([])
    const [month , setMonth] =useState([])
    const [precious , setPrecious] = useState([])
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(async ()=>{
    
        let res = await API.get(endpoints['all_booking'],{
            headers : {
                'Authorization' : `Bearer ${cookie.load('access_token')}`
            }
        },[])
        setList_booking(res.data.results)     
        
        // var date = new  Date().getFullYear()
        // var ele = date - 9
        // var month_resent = new Date().getMonth() + 1
        // for ( let i = ele ; i <= date ; i ++){
        //     year.push(i)
        // } 
        // for ( let i = 1 ; i <= 12 ; i ++){
        //     month.push(i)
        // } 
        // for ( let i = 1 ; i <= 4 ; i ++){
        //     precious.push(i)
        // }  
        // year.splice(10 , 99)
        // month.splice(10 , 99)
        // precious.splice(4 , 99)

        // if (defaults == 0 ){
        //     if (month_resent < 10){
        //         get_static_year(`${date}-${0}${month_resent}`)
        //     }else{
        //         get_static_year(`${date}-${month_resent}`)
        //     }
        // }
        
    },[change_precious,change_nor])


    const get_static_year =  (date) =>{
        let  get = async ()=>{
            let res = await API.post(endpoints['static_year'],{
                "date":date
            },{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            setCharData_year(res.data.map((u,index)=> count(u)))
            setCount_ticket(res.data.length)
            chartData_precious.push(res.data)

        }
        get()
    }

   

    let change_select_year = (event)=>{
        setChange_nor(change_nor+1)
        setDefaul(defaults+1)
        let res  = async ()=>{
            try {
                var a = []
                a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
                get_static_year(a[0])
            }catch(err){
                console.error(err)
            }
        }
        res()
      
    } 
    

    let change_select_month = (event)=>{
        setChange_nor(change_nor+1)
        setDefaul(defaults+1)

        var value =document.getElementById('change_static_year').value
        let res  = async ()=>{
            try {
                var a = 0
                a = (event.target.options[event.target.selectedIndex].getAttribute("value"))
                if (a >= 10 ){
                    get_static_year(`${value}-${a}`)
                }
                else{
                    get_static_year(`${value}-${0}${a}`)
                }
            }catch(err){
                console.error(err)
            }
        }
        res()
      
    }   
    var c = 0
    let change_select_precious = (event)=>{
        setChange_precious(change_precious+1)
        chartData_precious.splice(0,99) 
        setDefaul(defaults+1)

        var value =document.getElementById('change_static_precious').value
        let res  = async ()=>{
            try {
                var a = event.target.options[event.target.selectedIndex].getAttribute("value")       

                if ( a == 1 ){
                    for(let i = 1 ; i <= 3 ; i ++){
                        get_static_year(`${a}-${0}${i}`)
                    }
                }else if( a == 2 ){
                    for(let i = 4 ; i <= 6 ; i ++){
                        get_static_year(`${value}-${0}${i}`)
                    }   
                }else if( a == 3 ){
                    for(let i = 7 ; i <= 9 ; i ++){
                        get_static_year(`${value}-${0}${i}`)
                    }   
                }else if( a == 4 ){
                    for(let i = 10 ; i <= 12 ; i ++){
                        get_static_year(`${value}-${i}`)
                    }   
                }
            }catch(err){
                console.error(err)
            }
        }
        res()

    } 
    const onclick_year = () =>{
        setChange_nor(change_nor+1)
        setDefaul(defaults+1)

        chartData_precious.splice(0,99) 
        var a = new Date().getFullYear()
        get_static_year(a)
        console.log(year)
    }
    const onclick_month = () =>{
        setDefaul(defaults+1)

        chartData_precious.splice(0,99) 
        setChange_nor(change_nor+1)
        var date = new Date()
        var a = date.getFullYear()
        var b = date.getMonth() + 1
        get_static_year(`${a}-${b}`)

    }

    const onclick_precious = () =>{
        setChange_precious(change_precious+1)
        setDefaul(defaults+1)

        var date = new Date()
        var a = new Date().getFullYear()
        var b = date.getMonth() + 1
        // var b = 11
        chartData_precious.splice(0,99) 
        if (b <=3){
            for(let i = 1 ; i <= 3 ; i ++){
                get_static_year(`${a}-${0}${i}`)
            }
            
        }else if ( b<=6 && b > 3 ){
            for(let i = 4 ; i <= 6 ; i ++){
                get_static_year(`${a}-${0}${i}`)
            }
        }else if ( b<=9 && b > 6 ){
            for(let i = 7 ; i <= 9 ; i ++){
                get_static_year(`${a}-${0}${i}`)
            }
        }else if ( b<=12 && b > 9 ){
            for(let i = 10 ; i <= 12 ; i ++){
                get_static_year(`${a}-${i}`)
            }
        }
    }

    $('.footer-tour').hide()
    $('.intro').hide()
    
    function currencyFormat (num) {
        return  num.toFixed(0).replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.")
    }
    
    var a = 0 
    var b = 0
    if (list_booking!== null && list_booking !== undefined){
       
        if (chartData_precious !== '' && chartData_year!== undefined && change_precious !== 1 ){
            c = 0
            a = 0
            // console.log(c,a)
            console.log(chartData_precious)
            for (let i = 0 ; i < chartData_precious.length ; i++){
                
                for (let j  = 0 ; j < chartData_precious[i].length ; j ++ ){
                    // console.log(chartData_precious[i][j].total)
                    a += chartData_precious[i][j].total    
                    c += chartData_precious[i].length
                    b = c
                }                 
            }
        }
        else if(chartData_year !== '' && chartData_year!== undefined && change_nor !== 1 ){
          
            for (let i = 0 ; i < chartData_year.length ; i++){
                a += chartData_year[i] 
                b = count_ticket               
            }
        }
        else if(chartData_year !== '' && chartData_year!== undefined && defaults < 1 ){
            for (let i = 0 ; i < chartData_year.length ; i++){
                a += chartData_year[i] 
                b = count_ticket               
            }
        }

    }
    
   
if (list_booking !==null && list_booking !== undefined && a!== ''){
    return(
