import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container ,Button, Form} from "react-bootstrap";
import API, { AuthAPI, endpoints } from "../Configs/API";
import { useLocation } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"


export default function Destination () {
    
    const [tour , setTour] = useState([])
    const [name , setName] = useState(null)
    const [tags , setTags] = useState([])
    const [tags_add , setTags_add] = useState([])
    const [tags_selected , setTags_selected] = useState([])
    const [content , setContent] = useState([])
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)

    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)
    const location = useLocation()
    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)

    const user = useSelector(state => state.user.user)
    const image = useRef()



    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let query = location.search
        
            if(query ==='')
                query = `?page=${page}`
            else
                query = `&page=${page}`

        let res = await API.get(`${endpoints['tours']}${query}`)
        setTour(res.data.results)
        get_tags()
        setNext(res.data.next)
        console.log(res.data.results)
        setPrev(res.data.previous)
        // if (page >=2){
        //     setPrev(res.data.prev)
        // }
        // else{
        //     setPrev(null)
        // }

    },[location.search,page,next,prev,change])

    let paging=(inc)=>{
        setPage(page+inc)
    }

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('div').attr('id')
        get_tour_select(a)
       
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
    }


    const get_tour_select = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_total'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setActive(res.data.active)
            setContent(res.data.content)
            setTags_selected(res.data.tags)
            setID(res.data.id)

        }
        get_tour()
    }
    const update_tour = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("id" , ID)
                formdata.append("tags" , tags_add)

    
                let res = await API.patch(endpoints['tour_total'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                },[])
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <span className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </span>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <span className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </span>
            </>
        )
    }

    const get_tags =()=>{
        let tags = async()=>{
            try{
                let res = await API.get(endpoints['tag'])
                setTags(res.data.results)
            }catch(err){
                console.log(err)
            }
            
        }
        tags()
    }

    const add_tour = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("tags",tags_add)
                let res = await API.post(endpoints['tours'],formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data)
                tour.push(res.data)
                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

    let get_value = (event)=>{
        var a = []
        a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        setTags_add(a)
        console.log(a)
    }


    if (tags_selected !== '' && tags_selected !== null){
        if (tags_selected[0]){  
            $("#tags_info option").each(function(){
                if ($(this).text() === tags_selected[0].name){
                    $(this).attr("selected","selected");
                }
            })
        }
    }

    let get_and_del =(e) =>{
        var a = $(e.target).closest('div').attr('id')

        del_tour(a)
    }
    // XOng
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_total'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            tour.slice(res.data,1)
            setTour(tour)
            setChange(change+1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }


    var b = `<select>${tour.map((u,index) => <option value="" key="">{u.name}</option>)}</select>`
    const add_select_tag = ()=>{
        $('#tag_gr').append(b)
    }

    console.log(next)
    console.log(prev)

    return (
<>
<Container className="py-5">
<div class="tour-packed py-5">
    <div class="container">
        <div class="row ">
            <div class="col-md-12 heading-section text-center">
                <span class="subheading">Destination</span>
                <h2 class="under-subheading mb-4">Tour Destination</h2>
            </div>
        </div>
    </div>
</div>
<div className="list_tour">
    <Button className="btn_prev btn_tour" varient = "info" onClick={()=>paging(-1)} disabled={!prev}>&lt;&lt;</Button>
    
    <div className="list_tour_inner row">
        {tour.map( (u,index)=> <Tour key={index} tours = {u} info= {info_btn(openMOdal)} del_btn ={del_btn(get_and_del)} />)}
        <Col md={3} className="py-3">
            <div className="add_wrapper"  onClick={openMOdal_add}>
                <div className="add_center ">
                    <div className="add_icon" >
                        <span class="material-icons-outlined" >
                            add_circle_outline
                        </span>
                    </div>
                </div>
                <div className="footer_add">
                </div>
            </div>
        </Col>
    </div>

    <Button className="btn_next btn_tour" varient = "info" onClick={()=>paging(+1)} disabled={!next}>&gt;&gt;</Button>
    

</div>
  
</Container> 
<ToastContainer />
{/* -------------------------modal_info---------------------- */}
<Modal
isOpen={is_open}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
        <Form onSubmit={update_tour} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Content</label>
                <input type="text" value={content} onChange={(event)=>setContent(event.target.value)} />
            </div>
            <div className="form_group">
                <label for="username">Tags</label>
                <select id="tags_info" onChange={get_value} >
                    {tags.map((u,index) => <option  value={u.name} key={index}>{u.name}</option> )}
                </select>
            </div>
            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>

{/* ----------------------modal_add----------------------- */}

<Modal
isOpen={is_open_add}
onRequestClose={closeModal_add}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal_add}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper">
    <div className="form_wrapper">
        <h3 className="">Information's Tour</h3>
        <Form onSubmit={add_tour} className="form_register">
            <div className="form_group">
                <label for="username">Name</label>
                <input type="text" value={name} onChange={(event)=>setName(event.target.value)} />
            </div>
           
            <div className="form_group">
                <label for="username">Content</label>
                <input type="text" value={content} onChange={(event)=>setContent(event.target.value)} />
            </div>
            <div className="form_group" id="tag_gr">
                <label for="username">Tags</label>
                <select  onChange={get_value} >
                    {tags.map((u,index) => <option value={u.name} key={index}>{u.name}</option> )}
                </select>
                <span class="material-icons-outlined" onClick={add_select_tag}>
                    queue
                </span>
            </div>

            <div className="form_group">
                <label for="username">Active</label>
                <input type="checkbox" defaultChecked={active} onChange={(event)=>setActive(event.target.value)} />
            </div>
            <div className="label_">
                <label>Image</label>
            </div>
            <div className="input_">
                <Form.Control type="file" ref={image} />
            </div>
            <Button variant="primary" type="submit">
                <div className="save_btn d_fl_center">
                    <span class="material-icons-outlined">
                        save
                    </span>Save
                </div>
            </Button>
        </Form>
    </div>
</div>
</Modal>

</>
    )
}



function Tour(props){
    let path = ""

    path = `/listour/${props.tours.id}/list_detail/`



    return(
<>
<Col md={3} className="py-3">
    <div className="slick_img" style={{'backgroundImage' : `url(${props.tours.image})`}} id={props.tours.id}>
        {props.info}
        {props.del_btn}
        <div className="br_name">
            <div className="name_tour_br">
                {props.tours.name}
            </div>
        </div>
        <a href={path} className="src_detail">
        </a>
    </div>
</Col>
</>
    )
}


function add_select_tag_ft (tag,index){
    return(
<>
    <option  value={tag.name} key={index}>{tag.name}</option> }
</>
    )
}




       

