import { useState,useEffect } from "react"
import { Button,ButtonGroup, Container, Row } from "react-bootstrap";
import { useLocation } from "react-router";
import Apis, { endpoints } from '../Configs/Apis';
import MyCard from "../Layouts/Mycard";




export default function Home(){
    const [courses , setCourses] = useState([])
    const [tour , setTour] = useState([])
    const [prev , setPrev] = useState(false)
    const [next , setNext] = useState(false)
    const [page ,setPage] = useState(1) 
    const location = useLocation()
    
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async() => {
        let query = location.search
        if (query === "")
            query = `?page=${page}`
        else
            query += `&page=${page}`

        let res = await Apis.get(`${endpoints['courses']}${query}`).then(resp=>{
            setCourses(resp.data.results)
            setNext(resp.data.next !== null)
            setPrev(resp.data.prev !== null)
        })
        // let res = await Apis.get(`${endpoints['tours']}${query}`).then(resp=>{
        //     setTour(resp.data.results)
        // })
        console.log(query)
    },[location.search,page])

    const paging =(inc)=>{
        setPage(page+inc)
    }

    return(
<>
<ButtonGroup>
    <Button varient = "info" onClick={()=>paging(-1)} disabled={!prev}>&lt;&lt;</Button>
    <Button varient = "info" onClick={()=>paging(+1)} disabled={!next}>&gt;&gt;</Button>
</ButtonGroup>
<Container>
    <Row>
        {courses.map( (u,index)=> <MyCard key={index} obj = {u}/>)}
    </Row>
</Container>
</>


    )
}
