import { useEffect, useRef, useState } from "react"
import { Image } from "react-bootstrap"
import cookie from "react-cookies"
// import { useSelector } from "react-redux"
import API, { endpoints } from "../Configs/API"
import $ from 'jquery'
import { toast } from "react-toastify"
import Modal from 'react-modal'

export default function AccountDetail(){

    const [user,setUser] = useState(null)
    const [email,setEmail] = useState([])
    const [phone,setPhone] = useState([])
    const [address,setAddress] = useState([])
    const [last_name,setLast_name] =useState([])
    const [birthdate,setBirth_date] =useState([])
    const [is_staff,setIs_staff] = useState([true])

    const [booking_list_by_user,setBooking_list_by_user] = useState(null)
    const [change ,setChange] = useState(1)
    const avatar = useRef()

    const [modal_confirm, setModal_confirm] = useState(false);
    const [modal_update, setModal_update] = useState(false);
    const [is_open , setIs_open] = useState(false)
    const [is_open_update , setIs_open_update] = useState(false)

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(async ()=>{

        let res = await API.get(endpoints['current_user'],{
            headers : {
                'Authorization' : `Bearer ${cookie.load('access_token')}`
            }
        })
        let resbooking = await API.get(endpoints['get_booking_detail_by_user'],{
            headers : {
                'Authorization' : `Bearer ${cookie.load('access_token')}`
            }
        })
        setBooking_list_by_user(resbooking.data)
        setUser(res.data)
        setLast_name(res.data.last_name)            
        setAddress(res.data.address)
        setEmail(res.data.email)
        setPhone(res.data.phone)
        setBirth_date(res.data.birthdate)
    },[change])
    
    const change_all =()=>{
        $('.form-control').removeAttr('disabled');
    }

    const update_info =  (e)=>{
        e.preventDefault()
        let resRegist = async()=>{
            const formdata = new FormData()
            formdata.append("last_name" , last_name)
            formdata.append("email" , email)
            formdata.append("phone" , phone)
            formdata.append("address" , address)
            formdata.append("is_staff" , is_staff)
            formdata.append("birthdate" , birthdate)
            formdata.append("avatar" , avatar.current.files[0])
        
            try{
                let res = await API.post(endpoints['update'],formdata,{
                    headers:{
                        'Authorization': `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.info(res.data)
                toast.success('Thành Công!', {
                    position: "top-bottom",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
                setUser(res.data)
                setChange(10)

            }catch(er){
                console.error(er)
                toast.warning('Có lỗi rùi!', {
                    position: "top-bottom",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            }
        } 
        resRegist()
    }

    let openModal  = ()=>{
        setIs_open(true)
    }

    let closeModal  = ()=>{
      setIs_open(false)
    }
    let openModal_update  = ()=>{
        setIs_open_update(true)
        
    }
   
    let closeModal_update  = ()=>{
        setIs_open_update(false)
       
    }

   
   

    if (user!==null && user!== undefined && booking_list_by_user !== null && booking_list_by_user !== undefined 
        && booking_list_by_user!== ''){
            $('#header-top').css('background' ,'black')
            var a =  document.querySelectorAll('.table_booking tr')
            for ( let i = 1 ; i < a.length ; i ++){
                $(a[i]).mouseover(function(){
                    $(a[i]).css('border' , '1px rgb(224, 224, 224) solid')
                })
                $(a[i]).mouseleave(function(){
                    $(a[i]).css('border' , '1px solid white')
                })
            }
    return(
<>

<div class="profile py-5 mt-3">
    <div class="container py-5">
        <div class="row accordion " id="accordion-wrapper">
            <div class="col-md-2 px-1 option-profile">
                <div class="header-profile">
                    <div class="img-box">
                        <a href="/account_detail" className="src_profile">
                            <img src={user.avatar} alt="avatar" class="img-profile"/>
                        </a>
                    </div>

                    <div class="name">
                        <div class="name-wrapper">
                            <span>{user.last_name}</span>
                        </div>
                        <div class="icon">
                            <span class="material-icons-outlined">
                                edit
                            </span> Sửa Hồ Sơ
                        </div>
                    </div>
                </div>
                
            </div>

            <div class="col-md-10 pl-5 content-profile">
                <div class="">
                    <div class="header">
                        <h5>My Account</h5>
                        <p>Profile management information for account security</p>
                    </div>
                    <div class="form-detail-wrapper">
                        <form class="row form-detail" onSubmit={update_info}>
                            <div class="col-md-8">
                                <div class="detail">
                                <div class="form-group">
                                        <div class="form-left">
                                            <label>Username</label>
                                        </div>
                                        <div class="form-right">
                                            <p class="username">{user.username}</p>
                                        </div>
                                    </div>
                                    <div class="form-group">
                                        <div class="form-left">
                                            <label>Name</label>
                                        </div>
                                        <div class="form-right">
                                            <input type="text"  class="form-control"value={last_name}
                                                onChange={(event)=>setLast_name(event.target.value)} disabled/>
                                        </div>
                                        {/* <div className="">
                                            <span class="material-icons-outlined">
                                            build
                                            </span>
                                        </div> */}
                                    </div>
                                    <div class="form-group">
                                        <div class="form-left">
                                            <label>Email</label>
                                        </div>
                                        <div class="form-right">
                                            <input type="text" class="form-control" value={email} 
                                            onChange={(e)=>setEmail(e.target.value)}disabled/>
                                            
                                            {/* <div className="">
                                                <span>Thay đổi</span>
                                            </div> */}
                                        </div>
                                    </div>
                                    <div class="form-group">
                                        <div class="form-left">
                                            <label>Your Phone</label>
                                        </div>
                                        <div class="form-right">
                                            <input type="text" class="form-control" value={phone}
                                            onChange={(e)=>setPhone(e.target.value)} disabled/>
                                            {/* <div className="">
                                                <span>Thay đổi</span>
                                            </div> */}
                                        </div>
                                    </div>
                                    <div class="form-group">
                                        <div class="form-left">
                                            <label>Address</label>
                                        </div>
                                        <div class="form-right">
                                            <input type="text" class="form-control" value={address} 
                                            onChange={(e)=>setAddress(e.target.value)}disabled/>
                                        </div>
                                    </div>
                                    <div class="form-group">
                                        <div class="form-left">
                                            <label>BirthDate</label>
                                        </div>
                                        <div class="form-right">
                                            <input type="date" class="form-control" value={birthdate}
                                            onChange={(e)=>setBirth_date(e.target.value)} disabled/>
                                        </div>
                                    </div>
                                    <div className="">
                                        <button class="btn btn-primary btn_save">Save</button>
                                        <span class="btn btn-primary btn_change"onClick={change_all}>Change</span>
                                    </div>
                                    
                                </div>
                            </div>
                            <div class="col-md-4">
                                <div class="push_img">
                                    <label for="img_ava" className="label_img">
                                        <div class="upload_img">
                                            <img src={user.avatar} id="img-avatar" alt=""></img>
                                        </div>
                                        <span className="select_img">Select Image</span> 
                                    </label>
                                    <input type="file" id="img_ava" ref={avatar} />
                                    <p>Maximum capacity</p>
                                    <p>Format: .jpg .png</p>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
<div className="table_user py-5">
    <div className="container py-5">
        <table className="table_booking" >
            <thead>
                <tr style={{ border : '1px solid white'}}>
                    <th>ID</th>
                    <th>Username</th>
                    <th>Tour Name</th>
                    <th>Total</th>
                    <th>Booking Date</th>
                    <th>Booking status</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {booking_list_by_user.map((u,index) => <Item ></Item>)}
                
            </tbody>
           
        </table>
                                            
    </div>
</div>

<Modal
isOpen={modal_confirm}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper" style={{padding : '15px' , width : '500px'}} >
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
       
    </div>
</div>
</Modal>

<Modal
isOpen={modal_update}
onRequestClose={closeModal}
contentLabel="My dialog"
className="mymodal"
overlayClassName="myoverlay"
closeTimeoutMS={500}
ariaHideApp={false}
>
<div className="close_btn_wrapper" onClick={closeModal}>
    <div className="close_btn">
        <span class="material-icons-outlined">
            highlight_off
        </span>
    </div>    
</div>
<div className="modal_wrapper" style={{padding : '15px' , width : '500px'}} >
    <div className="form_wrapper">
        <h3 className="">Information's User</h3>
       
    </div>
</div>
</Modal>
</>
        )
    }
    else{
        return(
            <>    <h1>404</h1>
            </>
        )
    }
}

function Item (props,key){

    var a = props.obj.created_date
    var index2 =""
    a =  a.replace(/[ZT]/g,' ')
    var from_index = a.indexOf(' ')
    for(let i = from_index; i <= a.length;i++){
        
        index2 += a.charAt(i);
       
    }
    
    var r = a.replace(index2,"")

    function currencyFormat (num) {
        return  num.toFixed(0).replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.")
    }

    let cat_status = (status)=>{
        if(status === "Booking accepted"){
            return (<>
                <td className='accept_'>
                    <span>Success</span>
                </td>
            </>)
        }  
        if(status === "Booking processing"){
            return (<>
                <td className='process_'><span>Processing</span></td>
            </>)
        }  
        if(status === "Booking canceled"){
            return (<>
                <td className='cancel_'><span>Canceled</span></td>
            </>)
        }   
       
    }

return(
    <tr key={key}>
    <td id={props.id}>{props.id}</td>
    <td>{`${props.customer[3]} ${props.customer[4]} `}</td>
    <td>{props.tour_name}</td>
    <td>{currencyFormat(props.total)}</td>
    <td>{r}</td>
    {cat_status(props.status2)}
    <td>
        <div className="d-block">
            <div className="d-block">
                <div id="icon_repair" className="d-inline-block p-1" onClick={props.openMOdal}>
                    <span class="material-icons-outlined">
                        settings
                    </span>
                </div>
                <div id="icon_del" className="d-inline-block p-1" onClick={props.openMOdal_add}>
                    <span class="material-icons-outlined">
                        delete_outline
                    </span>
                </div>
            </div>
        </div>
    </td>
</tr>   


    )
}
