import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container ,Button, Form} from "react-bootstrap";
import API, { AuthAPI, endpoints } from "../Configs/API";
import { useLocation,useHistory } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"
import { Pagination } from '@mui/material';


export default function Destination () {
    
    const [tour , setTour] = useState([])
    const [name , setName] = useState(null)
    const [tags , setTags] = useState([])
    const [tags_add , setTags_add] = useState([])
    const [tags_selected , setTags_selected] = useState([])
    const [content , setContent] = useState([])
    const [active, setActive] = useState(true)
    const [ID, setID] = useState(true)

    const [name_search ,setName_search] = useState(null)
    const [tags_search , setTags_search] = useState([])

    const [page, setPage] = useState(1)
    const [prev, setPrev] = useState(null)
    const [next , setNext] = useState(null)
    const location = useLocation()
    const [change,setChange] = useState(1)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)


    const [count , setCount] = useState(0)
    const user = useSelector(state => state.user.user)
    const image = useRef()

    const history = useHistory()

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let query = location.search
        
            if(query ==='')
                query = `?page=${page}`
        //     else
        //         query = `&page=${page}`

        let res = await API.get(`${endpoints['tours']}${query}`)
        setTour(res.data.results)
        setNext(res.data.next)
        setPrev(res.data.previous)
        get_tags()
        setCount(res.data.count)
        console.log(res.data.results)

        // if (page >=2){
        //     setPrev(res.data.prev!==null)
        // }
        // else{
        //     setPrev(null)
        // }

    },[location.search,page,next,prev,change])

    let paging=(inc)=>{
        setPage(page+inc)
    }

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('div').attr('id')
        get_tour_select(a)
       
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
        
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setActive(true)
        setContent(null)
        setTags_selected(null)
        setTags_add([])  
    }


    const get_tour_select = (tourID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['tour_total'](tourID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            setName(res.data.name)
            setActive(res.data.active)
            setContent(res.data.content)
            setTags_selected(res.data.tags)
            setID(res.data.id)

        }
        get_tour()
    }


    const update_tour = (e)=>{
        e.preventDefault()

        let update = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                formdata.append("id" , ID)
    
                let res = await API.patch(endpoints['tour_total'](ID),formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                },[])
                console.log(res.data)
                closeModal()
                setChange(change+1)
            }catch(err){
                console.error(err)
            }
        }
        update()
    }
    let info_btn = (onclick)=>{
        return(
            <>
            <div className="icon_infor" onClick={onclick}>
                <span class="material-icons-outlined">
                info
                </span>
            </div>
            </>
        )
    }   

    let del_btn = (onclick)=>{
        return(
            <>
            <div className="icon_del" onClick={onclick}>
                <span class="material-icons-outlined">
                delete_outline
                </span>
            </div>
            </>
        )
    }

    const get_tags =()=>{
        let tags = async()=>{
            try{
                let res = await API.get(endpoints['tag'])
                setTags(res.data.results)
            }catch(err){
                console.log(err)
            }
            
        }
        tags()
    }
    const add_tag_tour = (tourID)=>{
        let add_tag = async () =>{
            let res = await API.post(endpoints['tour_total_add_tag'](tourID),{
                'tags' : tags_add
            },{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
            console.log(res.data)
           
        }
        add_tag()
    }
    const add_tour = (e)=>{
        e.preventDefault()
        let add = async ()=>{
            try{
                const formdata  = new FormData()
                formdata.append("name",name)
                formdata.append("content",content)
                formdata.append("image",image.current.files[0])
                formdata.append("active",active)
                // formdata.append("tags",tags_add)
                let res = await API.post(endpoints['tours'],formdata,{
                    headers : {
                        'Authorization' : `Bearer ${cookie.load('access_token')}`
                    }
                })
                console.log(res.data.id)
                tour.push(res.data)
                add_tag_tour(res.data.id)

                closeModal_add()
            }catch(er){
                console.error(er)
            }
           

        }
        add()
    }

    let get_value = (event)=>{
        if (tags_add.length >= 1 ){
            tags_add.splice(1)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))

    }

    let get_value2 = (event)=>{
        if (tags_add.length >= 2 ){
            tags_add.splice(2)
        }
        // var a = []
        // a.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
        tags_add.push(event.target.options[event.target.selectedIndex].getAttribute("value"))
    }



    if (tags_selected !== '' && tags_selected !== null){
        if (tags_selected[0]){  
            $("#tags_info option").each(function(){
                if ($(this).text() === tags_selected[0].name){
                    $(this).attr("selected","selected");
                }
            })
        }
    }

    let get_and_del =(e) =>{
        var a = $(e.target).closest('div').attr('id')

        del_tour(a)
    }
    // XOng
    const del_tour = async (tourID)=>{
        try{
            let res = await API.delete(endpoints['tour_total'](tourID),{
                headers : {
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            },[])
            tour.slice(res.data,1)
            setTour(tour)
            setChange(change+1)  
            toast.success('Delete success!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(er){
            toast.warn('Có lỗi kìa má ôi!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    }
   

    const search_form = (e)=>{
        e.preventDefault()
        if (window.location.reload()){
            setTimeout(history.push(`/listour/?q=${name_search}`),2000)
            // history.push(`/listour/?q=${name_search}`)
        }
    }

    const change_tag_search =(event)=>{
        setTags_search(event.target.options[event.target.selectedIndex].getAttribute("value"))
    
    }


    if (tour !== null && tour !== '' && tour !== undefined ){
        $('.option_wrapper').hide()
        var a = document.querySelectorAll('.slick_img')
        var b = document.querySelectorAll('.option_wrapper')
        for(let i = 0 ; i< a.length ; i ++){
            $(a[i]).mousemove(function(){
                $(b[i]).show()  
            })
            $(a[i]).mouseleave(function(){
                $(b[i]).hide()  
            })
        }
        

