    const [date , setDate]  = useState(null)
    const [price , setPrice] = useState(null)
    const [discount_search ,setDiscount_search]  = useState(1)
    const [time_start_search , setTime_start_search] = useState([])
    const [duration_from ,setDuration_from] = useState([])
    const [duration_to,setDuration_to] = useState([])

    
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect( async()=>{
        let res = await API.get(`${endpoints['list_tour_detail'](tourID)}${location.search}`)
        setList_detail(res.data)

        
        let res_transport = await API.get(endpoints['get_transport'])
        setTransport(res_transport.data.results)
       

        let res_tour_by_id = await API.get(endpoints['tour_total_by_id'](tourID))
        setTour_by_id(res_tour_by_id.data)

    },[change,location.search,tourID])

   

    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('.slick_img').attr('id')
    }

    let closeModal  = ()=>{
        setIs_open(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
    }

    let openMOdal_add  = ()=>{
        setIs_open_add(true)
       
    }
   
    let closeModal_add  = ()=>{
        setIs_open_add(false)
        setName(null)
        setPrice_room(null)
        setPrice_tour(null)
        setDiscount(null)
        setDuration(null)
        setTime_start(null)  
    }
