import axios from "axios";


export let endpoints = {
    "categories": "/categories/",
    "courses" : "/courses/",
    "lessons" : (courseId) => `/courses/${courseId}/lessons/`,
    "lesson-detail": (lessonId) => `/lessons/${lessonId}/`
}

export default axios.create({
    baseURL: "http://127.0.0.1:8000/"
})
