const initState = {
    'user':localStorage.getItem('user'),
    'admin':null,
    'staff':null,
}


const userReducer = (state = initState,action) =>{
    switch(action.type){
        case "USER_LOGIN":
            return {
                ...state,
                'user': action.payload
            }
        // case "ADMIN_LOGIN":
