import { useEffect, useRef, useState } from "react"
import { Col, Image,Form, Button } from "react-bootstrap"
import API, { endpoints } from "../Configs/API"
import { useLocation } from "react-router";
import $ from 'jquery'; 
import { useSelector } from "react-redux";
import { toast, ToastContainer } from "react-toastify";
import Modal from "react-modal";
import cookie from "react-cookies"
import '../CSS/Blog.css'
import '@mui/material'
import OwlCarousel from 'react-owl-carousel'
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';



export default function Blog (){
    
    const [blog_main,setBlog_main] = useState([])    
    const [blog_first,setBlog_first] = useState([])    
    const [list_blog,setList_blog] = useState([])    

    const [blog_second2,setBlog_second2] = useState([])    
    const [blog_second3,setBlog_second3] = useState([])    


    const [content,setContent] = useState([])    
    const [active,setActive] = useState(true)    
    const [decription,setdecription] = useState([])    
    const [name,setName] = useState(null)    
    const [ID, setID] = useState(null)
    const [tag,setTag] = useState([])
    const [tag_add,setTag_add] = useState([])
    
    const location =  useLocation()
    const image = useRef()

    const [type_like , setType_like] = useState(0)
    const [like_count,setLike_count] = useState()
    
    const [tour_detail,setTourt_detail] =useState([])

    const [change,setChange] = useState(0)
    const [is_open , setIs_open] = useState(false)
    const [is_open_add , setIs_open_add] = useState(false)

    const [tag_selected , setTtag_selected] = useState([])

    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(async() => {
        try{
            let res_listblog = await API.get(`${endpoints['get_blog_all']}${location.search}`)
            setList_blog(res_listblog.data.results)
           
            // setBlog_second(res.data.results.slice(5,res.data.length))

            let restour_detail = await API.get(`${endpoints['tour_detail_all']}`,[])
            setTourt_detail(restour_detail.data.results)

            let res_tag = await API.get(endpoints['get_tag_blog'])
            setTag(res_tag.data.results)

         
            let get_blog_by_tag2 = async (name)=>{
                let res = await API.get(`${endpoints['get_blog_all']}?tag=${name}`,[])
                setBlog_second2(res.data.results)
            }
            let get_blog_by_tag3 = async (name)=>{
                let res = await API.get(`${endpoints['get_blog_all']}?tag=${name}`,[])
                setBlog_second3(res.data.results)
            }
            let res = await API.get(`${endpoints['get_blog_all']}?tag=${res_tag.data.results[0].name}`)
            setBlog_main(res.data.results.slice(0,1))
            setBlog_first(res.data.results.slice(1,4))

            get_blog_by_tag2(res_tag.data.results[1].name)
            get_blog_by_tag3(res_tag.data.results[2].name)
            
        }
        catch(er){
            console.error(er)
        }
       
    },[change,location.search]);
    window.onload = function(){
       
    }
    
    let openMOdal  = (e)=>{
        setIs_open(true)
        var a = $(e.target).closest('.js_blog').attr('id')
        get_blog_by_id_selected(a)
    }
    const get_blog_by_id_selected = (blogID)=>{
        let get_tour = async () =>{
            let res = await API.get(endpoints['get_blog_by_id'](blogID),{
                headers : { 
                    'Authorization' : `Bearer ${cookie.load('access_token')}`
                }
            })
